/// <summary>
/// Clones the inquiry from the <c>SalesTable</c> form.
/// </summary>
void  clicked()
{
    // Only display the continuity data option when it is a continuity order.
    if (mcrSalesTable.ContinuityOrder)
    {
        continuityData.enabled(true);
    }
    else
    {
        continuityData.enabled(false);
    }

    // enable the button if return order is allowed for the current order.
    if ((salesTable.SalesType != SalesType::ReturnItem) && (element.isReturnOrderAllowed(salesTable.SalesId)))
    {
        soCreateReturn.enabled(true);
    }
    else
    {
        soCreateReturn.enabled(false);
    }

    super();
}

