{
    Args args = new Args();
    FormRun         menuFunction;
    SalesTable      currentRecord;
    // save currently selected record
    currentRecord = salesTable.data();

    args.caller(element);
    args.record(salesTable);

    menuFunction = new MenuFunction(menuitemDisplayStr(MCRHoldCodeTransSOManage), MenuItemType::Display).create(args);
    menuFunction.run();
