    formRun.run();
    formRun.wait();

    // If the line hasn't been saved, there is no way more lines could have
    // been added, so there is no need to re-execute the query.
    if (salesLine.RecId)
    {
        // The Item Info screen will sometimes add a line through upsell/Cross-sell
        // so the sales line needs to be refreshed after the corm closes.
        salesLine_ds.executeQuery();
        salesLine_ds.last();
    }
}

/// <summary>
/// Passes additional parameters to trigger additional code in
/// the <c>SalesCalcTax_Sales</c> class that allows taxes for invoiced sales orders
/// to be displayed.
/// </summary>
void clicked()
{
    // Pass the parameter to totals for Vertex that this is a quote for taxes.
    SalesTotals::showTaxLine(menuitemDisplayStr(TaxTmpWorkSalesOrder), salesTable, salesLine);
    salesLine_ds.reread();
}

public void jumpRef()
{
    Args    args;
    FormRun formRun ;
    SalesTable  salesTableOrigin;
    InventTransOrigin   inventTransOrigin;

    inventTransOrigin = inventTransOrigin::findByInventTransId(SalesLine.InventTransIdReturn);
    salesTableOrigin = SalesTable::find(inventTransOrigin.ReferenceId);

    args = new Args();
    args.record(salesTableOrigin);
    formRun = new MenuFunction(menuitemDisplayStr(SalesTable), MenuItemType::Display).create(args);
    formRun.run();
    formRun.wait();

    super();
}

void clicked()
{
    // Pass in true so that the continuity cust info form will always be disabled
    // from customer service.
    MCRContinuityCustHeader::callContinuityCustHeaderForm(invoiceSalesLine.InventTransId, "", true);
    super();
}

