[Form]
public class MCRDropShipWorkbench extends FormRun
{
    MCRDropShipStatus           selectionDropShipStatus;
    Map                         canIncludeLineMap;
    TmpPurchLinePrice           tmpPurchLinePrice;
    boolean                     calledFromInitFields;
    PurchCreateFromSalesOrder   purchCreate;

}

public void init()
{
    QueryBuildDataSource    purchTableDS;
    QueryBuildDataSource    purchLineDS;
    QueryBuildRange         queryBuildRange;
    Query                   query = new Query();

    purchTableDS = query.addDataSource(tableNum(PurchTable), tableStr(PurchTable));
    purchLineDS = purchTableDS.addDataSource(tableNum(PurchLine));

    purchTableDS.addRange(fieldNum(PurchTable, mcrDropShipment)).value(SysQuery::value(NoYes::Yes));
    purchLineDS.addRange(fieldNum(PurchLine, mcrDropShipStatus)).value(SysQuery::value(selectionDropShipStatus));
    purchLineDS.addRange(fieldNum(PurchLine, IsDeleted)).value(SysQuery::value(NoYes::No));

    purchLineDS.relations(true);
    purchLineDS.joinMode(JoinMode::ExistsJoin);

    queryBuildRange = purchTableDS.addRange(fieldNum(PurchTable, DataAreaId));
    queryBuildRange.value(strFmt('(%1 == %2) || (%3 == %4)',
        SysQuery::value(fieldStr(PurchTable, InterCompanyOrder)),
        SysQuery::value(enum2int(NoYes::No)),
        SysQuery::value(fieldStr(PurchTable, InterCompanyCompanyId)),
        SysQuery::valueEmptyString()));

    this.query(query);
    super();
}

public int active()
{
    int ret;

    ret = super();

    if (MCRHoldCodeTrans::findActiveOrderHolds(purchTable.PurchId))
    {
        releaseMenuItemButton.enabled(false);
        partialShipMenuItemButton.enabled(false);
        shippingMenuItemButton.enabled(false);
    }
    else
    {
        releaseMenuItemButton.enabled(true);
        partialShipMenuItemButton.enabled(true);
        shippingMenuItemButton.enabled(true);
    }

    return ret;
}

public void init()
{
    QueryBuildDataSource    salesTableDS;
    QueryBuildDataSource    salesLineDS;
    QueryBuildDataSource    queryBuildDataSourceMCRSalesLineDropShipment;
    QueryBuildDataSource    queryBuildDataSourceInterCompanyPONotExists;
    QueryBuildRange         queryBuildRange;
    Query                   query = new Query();

    salesTableDS = query.addDataSource(tableNum(SalesTable), tableStr(SalesTable));
    salesLineDS = salesTableDS.addDataSource(tableNum(SalesLine));

    salesTableDS.addRange(fieldNum(SalesTable, mcrOrderStopped )).value(SysQuery::value(NoYes::No));
    salesTableDS.addRange(fieldNum(SalesTable, InterCompanyOrder)).value(SysQuery::value(NoYes::No));
    salesLineDS.addRange(fieldNum(SalesLine, LineDeliveryType)).value(SysQuery::valueNot(LineDeliveryType::OrderLineWithMultipleDeliveries));
    salesLineDS.addRange(fieldNum(SalesLine, SalesStatus)).value(SysQuery::value(SalesStatus::Backorder));

    queryBuildDataSourceMCRSalesLineDropShipment = salesLineDS.addDataSource(tableNum(MCRSalesLineDropShipment));
    queryBuildDataSourceMCRSalesLineDropShipment.relations(true);
    queryBuildDataSourceMCRSalesLineDropShipment.joinMode(JoinMode::InnerJoin);
    queryBuildDataSourceMCRSalesLineDropShipment.addRange(fieldNum(MCRSalesLineDropShipment, DropShipment)).value(SysQuery::value(NoYes::Yes));
    queryBuildDataSourceMCRSalesLineDropShipment.addRange(fieldNum(MCRSalesLineDropShipment, DropShipStatus)).value(SysQuery::value(MCRDropShipStatus::ToBeDropShipped));

    salesLineDS.relations(true);
    salesLineDS.joinMode(JoinMode::ExistsJoin);

    queryBuildDataSourceInterCompanyPONotExists = salesTableDS.addDataSource(tableNum(PurchTable));
    queryBuildDataSourceInterCompanyPONotExists.joinMode(JoinMode::NoExistsJoin);
    queryBuildDataSourceInterCompanyPONotExists.relations(false);
    queryBuildDataSourceInterCompanyPONotExists.addLink(fieldNum(SalesTable, PurchId), fieldNum(PurchTable, PurchId));
    queryBuildDataSourceInterCompanyPONotExists.addRange(fieldNum(PurchTable, InterCompanyOrder)).value(SysQuery::value(NoYes::Yes));

    queryBuildRange = salesTableDS.addRange(fieldNum(SalesTable, DataAreaId));
    queryBuildRange.value(strFmt('(%1 == %2) || (%3 == %4)',
        SysQuery::value(fieldStr(SalesTable, InterCompanyOrder)),
        SysQuery::value(enum2int(NoYes::No)),
        SysQuery::value(fieldStr(SalesTable, InterCompanyCompanyId)),
        SysQuery::valueEmptyString()));

    this.query(query);

    super();
    salesTable_ds.first();
    salesLine_ds.executeQuery();
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    MCRHoldOrderDisplayColor mcrHoldOrderDisplayColor;

    SalesTable salesTableLocal =  _record;

    mcrHoldOrderDisplayColor = SalesParameters::find().mcrHoldOrderDisplayColor;

    if (mcrHoldOrderDisplayColor
        &&  MCRHoldCodeTrans::findActiveOrderHolds(salesTableLocal.SalesId))
    {
        _options.backColor(mcrHoldOrderDisplayColor);
    }
    else
    {
         _options.clearBackColor();
    }

    super(_record, _options);
}

public void init()
{
    QueryBuildDataSource    salesLineDS;

    super();

    salesLineDS = salesLine_ds.query().dataSourceTable(tableNum(SalesLine));
    salesLineDS.addRange(fieldNum(SalesLine, LineDeliveryType)).value('!' + enum2str(LineDeliveryType::OrderLineWithMultipleDeliveries));
}

public void init()
{
    QueryBuildDataSource    dropShipmentSalesLineDS;

    super();

    dropShipmentSalesLineDS = salesLine_ds.query().dataSourceTable(tableNum(MCRSalesLineDropShipment));
    dropShipmentSalesLineDS.addRange(fieldNum(MCRSalesLineDropShipment, DropShipment)).value(queryValue(NoYes::Yes));
    dropShipmentSalesLineDS.addRange(fieldNum(MCRSalesLineDropShipment, DropShipStatus)).value(enum2str(MCRDropShipStatus::ToBeDropShipped));
}

/// <summary>
///     Creates a drop ship purchase order for every selected sales order record.
/// </summary>
void clicked()
{
    SalesTable   salesTableLocal;
    SalesLine    salesLineLocal;
    Set          salesLineRecIdSet;
    Args         args                    = new Args();
    purchCreate = PurchCreateFromSalesOrder::newPurchCreateFromSalesOrder(TradeLineDlvType::DropShip);
    purchCreate.parmSearchForAgreements(SearchForAgreements.value());

    super();

    // NOTE:  if only 1 row is selected in the grid, the row is not marked
    if (!salesTable_ds.anyMarked())
    {
        // mark the currently selected row (does nothing if no row selected)
        salesTable_ds.mark(1);
    }
    if (salesTable_ds.recordsMarked().lastIndex() == 1)
    {
        if (salesLine_ds.anyMarked())
        {
            salesLineLocal = salesLine_ds.getFirst(1);
            salesLineRecIdSet = new Set(Types::Int64);
            while (salesLineLocal)
            {
                salesLineRecIdSet.add(salesLineLocal.RecId);
                salesLineLocal = salesLine_ds.getNext();
            }
        }
    }

    // loop through selected rows...
    salesTableLocal = salesTable_ds.getFirst(1);
    while (salesTableLocal)
    {
        salesTableLocal.reread();
        if (salesTableLocal.mcrOrderStopped != NoYes::Yes)
        {
            if (PurchDropShipCreateHandleErrorsFlight::instance().isEnabled())
            {
                MCRCreatePurchOrderForDropShipments::createDirectDeliveryPurchaseOrder(purchCreate, 
                    salesTableLocal, 
                    salesLineRecIdSet);
            }
            else
            {
                purchCreate.parmTransferDeliveryAddressFromAgreement(false);
                purchCreate.parmTransferAddress(true);
                purchCreate.tradeLineDlvType(TradeLineDlvType::DropShip);
                purchCreate.mcrDropShipment(NoYes::Yes);
                purchCreate.parmSalesTable(salesTableLocal);
                purchCreate.parmSalesLineRecIdSet(salesLineRecIdSet);
                purchCreate.parmCallerRecord(salesTableLocal);

                if (!purchCreate.mcrDropShipCreateTmpFrmVirtual())
                {
                    return;
                }

                purchCreate.parmPOTransDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
                purchCreate.runOperation();
            }
        }
        else
        {
            error(strFmt("@SYS4010029", salesTableLocal.SalesId));
        }

        salesTableLocal = salesTable_ds.getNext();
    }

    // refresh the grid
    salesTable_ds.executeQuery();
}

public void pageActivated()
{
    super();
    // Set the drop ship status
    selectionDropShipStatus = MCRDropShipStatus::POCreated;
    purchTable_ds.init();
    purchTable_ds.executeQuery();
    purchTable_ds.first();

    // Should exclude the deleted one.
    purchLine_ds.queryBuildDataSource().addRange(fieldNum(PurchLine, IsDeleted)).value(SysQuery::value(NoYes::No));
    purchLine_ds.executeQuery();
}

