{
    Args args = new Args();
    MenuFunction menuFunction = new MenuFunction(menuitemActionStr(PurchFormLetter_Confirm), MenuItemType::Action);

    super();

    if (purchTable.DocumentState == VersioningDocumentState::Approved)
