///     for the record that is passed in.
/// </summary>
/// <returns>
///     true if an active hold exists; otherwise, false.
/// </returns>
boolean checkForActiveHolds()
{
    return MCRHoldCodeTrans::findActiveOrderHolds(salesTable.SalesId);
}

public void checkOutSelectedHolds()
{
    MCRHoldCodeTrans        mcrHoldCodeTransCurrent;
    MultiSelectionHelper    selection = MultiSelectionHelper::construct();

    selection.parmDatasource(MCRHoldCodeTrans_ds);
    mcrHoldCodeTransCurrent = selection.getFirst();
    while (mcrHoldCodeTransCurrent)
    {
        mcrHoldCodeTransCurrent.checkOutHold();

        mcrHoldCodeTransCurrent = selection.getNext();
    }

    MCRHoldCodeTrans_ds.research(true);
}

/// <summary>
///     Clears all of the selected hold codes.
/// </summary>
/// <param name="_submit">
///     <c>Boolean</c> to submit the order for further processing.
/// </param>
void clearAllMarked(boolean _submit = false)
{
    MCRHoldCodeTrans        mcrHoldCodeTransCurrent;
    MultiSelectionHelper    selection = MultiSelectionHelper::construct();

    selection.parmDatasource(MCRHoldCodeTrans_ds);
    mcrHoldCodeTransCurrent = selection.getFirst();
    while (mcrHoldCodeTransCurrent)
    {
        try
        {
            ttsbegin;
            mcrHoldCodeTransCurrent.clearHold();
            ttscommit;

            // submit the sales orders if paramters are set
            if (RetailMCRChannelTable::mcrEnableOrderCompletion(SalesTable::find(mcrHoldCodeTransCurrent.InventRefId)))
            {
                // If the user has set a parameter to submit the sales order for
                // further processing on clear, ensure there are no other holds to clear and
                // submit the order.
                if (_submit || mcrOrderParameters.MCRSubmitOnClear)
                {
                    SalesTable salesTableCurrent = SalesTable::find(mcrHoldCodeTransCurrent.InventRefId);
                    if (!MCRHoldCodeTrans::findActiveOrderHolds(salesTableCurrent.SalesId))
                    {
                        MCRCustPaymTotals mcrCustPaymTotals = MCRCustPaymTotals::construct(salesTableCurrent.tableId, salesTableCurrent.recid);
                        boolean paymentExist = mcrCustPaymTotals.validateAuthorize(true);
                        // Since this involves submitting the order, it should be ensured that
                        // a payment exists before trying to submit the order
                        if (paymentExist)
                        {
                            element.submitOrder(salesTableCurrent.SalesId);
                        }
                    }
                }
            }
        }
        catch
        {
            // keep going
            global::exceptionTextFallThrough();
        }
        mcrHoldCodeTransCurrent = selection.getNext();
    }

    MCRHoldCodeTrans_ds.research(true);
}

