public void init()
{
    salesParameters     = SalesParameters::find();
    super();

    // When the args have lookupTable = <c>SalesTable</c>, lookupField = CustAccount, and lookupValue is not empty then filter by CustAccount
    if (element.args() && element.args().lookupTable() == tableNum(SalesTable)
        && element.args().lookupField() == fieldNum(SalesTable, CustAccount) && element.args().lookupValue())
    {
        SysQuery::findOrCreateRange(salesTable_ds.queryBuildDataSource(), element.args().lookupField()).value(SysQuery::value(element.args().lookupValue()));

        // Since orginially this is defined as an outer join it needs to be changed to an inner join to propigate the filtering
        MCRHoldCodeTrans_ds.query().dataSourceTable(tableNum(SalesTable)).joinMode(JoinMode::InnerJoin);

        // clear the lookup values since it will try to use the values on the MCRHoldCodeTrans_ds
        element.args().lookupTable(0);
        element.args().lookupField(0);
        element.args().lookupValue('');
    }

    element.initControls();
}

/// <summary>
///     Shows and hides the form controls based on parameters.
/// </summary>
void initControls()
{
    SalesTable  salesTableActive;
    boolean     isCallCenter;

    MCRHoldCodeTrans_ds.allowCreate(false);

    // Read and save the sales parameters.
    mcrOrderParameters = MCROrderParameters::find();

    if (element.args().parmEnumType())
    {
        switch ( element.args().parmEnum())
        {
            case MCRHoldCodeTransMode::SOCreateHold:

                // Hide all Sales order controls, used for inquiry and release processes
                MCRSalesOrderModification.visible(false);
                // Allow to create new sales order holds
                MCRHoldCodeTrans_ds.allowCreate(true);
                // Submit Open Order Button
                MCREndSalesOrderOverviewTab.visible(false);

                // Don't need to see the submit options because the application will automatically
                // submit the order if the parameter is set.
                if (mcrOrderParameters.MCRSubmitOnClear)
                {
                    ClearAndSubmit.visible(false);
                }

                element.setCheckOutControls();

                salesTableActive = element.args().record();

                if (salesTableActive)
                {
                    isCallCenter = salesTableActive.mcrIsCallCenter();
                    MCRHoldCodeTrans_ds.object(fieldNum(MCRHoldCodeTrans, MCRReasonCode)).visible(isCallCenter);
                }

                break;

            case MCRHoldCodeTransMode::SOManageHold:

                if (element.args().record())
                {
                    if (element.args().record().TableId == tableNum(SalesTable))
                    {
                        element.setCheckOutControls();
                    }
                }

                // Submit open order button.
                MCREndSalesOrderOverviewTab.visible(true);

                // Hide and show appropriate button groups on the overview tab.

                // Don't need to see the options because the application will automatically
                // submit the order if the parameter is set.
                if (mcrOrderParameters.MCRSubmitOnClear)
                {
                    ClearAndSubmit.visible(false);
                }

                break;

            default:

                break;
        }
    }
    // Do not allow delete if active holds exist.
    if (element.checkForActiveHolds())
    {
        MCRHoldCodeTrans_ds.allowDelete(false);
    }
    else
    {
        MCRHoldCodeTrans_ds.allowDelete(true);
    }
    // Disable the clear and submit buttons. In case of the
    // MCRSubmitOnClear being turned on, this button will submit
    // the orders automatically when the last hold is cleared. If the
    // parameter is not on, then the clear and submit will get enabled when
    // the last hold code is being cleared.
    if (mcrOrderParameters.MCRSubmitOnClear)
    {
        Clearandsubmit.enabled(false);
    }
}

