/// <summary>
///     Overrides the hold check out.
/// </summary>
/// <param name="_mcrHoldCodeTransDS">
/// The hold code transaction record data source.
/// </param>
/// <remarks>
///     When button override check out is clicked, invoke the form to
///     receive the input from the user. The user id that is provided
///     by the user is then updated to all the selected records. If the
///     overridden userid is a blank, this mean that the hold code record
///     is being returned to the available pool of records, clear the checked
///     out flags and update date and time accordingly.
/// </remarks>
void overrideCheckOut(FormDatasource _mcrHoldCodeTransDS)
{
    MCRHoldCodeTrans localMCRHoldCodeTrans;
    UserId overriddenUser;
    boolean overrideFormCloseOK;

    // Get the first marked record, call the override form
    // obtain the users response to the override user
    // save this returned value and update the records that
    // are selected.
    if (!_mcrHoldCodeTransDS.anyMarked())
    {
        // Marks the currently selected row (does nothing if no row selected).
        _mcrHoldCodeTransDS.mark(1);
    }
    localMCRHoldCodeTrans = _mcrHoldCodeTransDS.getFirst(1);
    overrideFormCloseOK = element.launchCheckOutOverrideForm(localMCRHoldCodeTrans);

    if (overrideFormCloseOK)
    {
        overriddenUser = localMCRHoldCodeTrans.MCRCheckedOutTo;

        while (localMCRHoldCodeTrans)
        {
            try
            {
                ttsbegin;
                // Check to see if the overridden user is a blank
                // remove the checked out flag if it is
                if (overriddenUser != "")
                {
                    localMCRHoldCodeTrans.checkOutHold(overriddenUser);
                }
                else
                {
                    localMCRHoldCodeTrans.clearCheckOut();
                }
                ttscommit;
            }
            catch
            {
                global::exceptionTextFallThrough();
            }
            localMCRHoldCodeTrans = _mcrHoldCodeTransDS.getNext();
        }

        // refresh the grid
        _mcrHoldCodeTransDS.research(true);
    }
}

/// <summary>
///     Allows the orders options to be enabled regardless
///     of whether the hold is checked in/out.
/// </summary>
void setCheckOutControls()
{
    // Check the record to see if the current user has checked
    // out the hold. if current user has not checked it out, disable
    // the check out and clear buttons.
    if (MCRHoldCodeTrans.MCRCheckedOutTo != "")
    {
        if (MCRHoldCodeTrans.MCRCheckedOutTo != CurUserId())
        {
            MCRHoldCodeTrans_ds.allowEdit(false);
            ClearHold.enabled(false);
            Checkout.enabled(false);
            ClearCheckOut.enabled(false);
        }
        else
        {
            MCRHoldCodeTrans_ds.allowEdit(true);
            ClearHold.enabled(true);
            ClearCheckout.enabled(true);
            CheckOut.enabled(false);
        }
    }
    else
    {
        MCRHoldCodeTrans_ds.allowEdit(true);
        ClearHold.enabled(true);
        ClearCheckout.enabled(false);
        CheckOut.enabled(true);
    }
}

void submitOrder(SalesId _salesId)
{
    boolean             endOrderSuccess = false;
    SalesTable          salesTableSubmit;
    MCROrderRecapStatus orderRecapRet;

    FormRun formRun;
    formrun = element.args().caller();

    if (!MCRHoldCodeTrans::findActiveOrderHolds(_salesId))
    {
        if (formrun)
        {
            if (formrun.name() != formstr(MCRSalesOrderRecap))
            {
                endOrderSuccess = MCREndOrder::endOrder(_salesId);
                if (endOrderSuccess)
                {
                    if (element.args().record())
                    {
                        if (element.args().record().TableId == tableNum(CustTable)
                        || element.args().record().TableId == tableNum(SalesTable))
                        {
                            element.close();
                        }
                    }
                }
            }
            else
            {
                salesTableSubmit = SalesTable::find(_salesId);
                orderRecapRet = MCREndOrder::orderRecap(salesTableSubmit);
                element.closeok();
                switch (orderRecapRet)
                {
                    case MCROrderRecapStatus::Close:
                    case MCROrderRecapStatus::CloseAuth:
                    case MCROrderRecapStatus::OpenNoAuth:
                        formrun.closeOk();
                    break;
                    case MCROrderRecapStatus::OpenError:
                        formrun.closeCancel();
                    break;
                }
            }
        }
        else
        {
            endOrderSuccess = MCREndOrder::endOrder(_salesId);
            if (endOrderSuccess)
            {
                if (element.args().record())
                {
                    if (element.args().record().TableId == tableNum(CustTable)
                    || element.args().record().TableId == tableNum(SalesTable))
                    {
                        element.close();
                    }
                }
            }
        }
    }
    else
    {
        throw error("@MCR22891");
    }
}

void setQuery()
{
    source = MCRHoldCodeTrans_ds.query().dataSourceTable(tableNum(MCRHoldCodeTrans));
    clearedHoldCode = source.addRange(fieldNum(MCRHoldCodeTrans, MCRCleared));
    clearedHoldCode.value(enum2str(NoYes::No));
    checkedOutStatus = source.addRange(fieldNum(MCRHoldCodeTrans, MCRCheckedOut));
    checkedOutStatus.value("");
    source.addSortField(fieldnum(MCRHoldCodeTrans, InventRefid), sortorder::Descending);
    source.addSortField(fieldnum(MCRHoldCodeTrans, CreatedDateTime), sortorder::Descending);
}

public int active()
{
    int ret;
    SalesTable salesTableActive;

    ret = super();
    salesTableActive = SalesTable::find(MCRHoldCodeTrans.InventRefId);

    element.setCheckOutControls();

    //     Disables the <c>MCRCleared</c> field if the hold is cleared and
    //     disables the submit open order button for all orders other then "Open".
    switch ( element.args().parmEnum())
    {
        case MCRHoldCodeTransMode::SOCreateHold:
        case MCRHoldCodeTransMode::SOManageHold:

            // Enable the "submit open order" button for open order.
            // This button is disabled for all other orders.
            if (salesTableActive.SalesStatus == SalesStatus::Backorder)
            {
                MCREndSalesOrderOverviewTab.enabled(true);
            }
            else
            {
                MCREndSalesOrderOverviewTab.enabled(false);
            }

        default:
            break;
    }
    // Disable the MCRCleared Field if the hold is cleared
    if (MCRHoldCodeTrans.MCRCleared)
    {
        MCRSalesOrderHoldCode_MCRCleared.enabled(false);
    }
    else
    {
        MCRSalesOrderHoldCode_MCRCleared.enabled(true);
    }
    // Check to see if any active holds exist. If there are
    // disable delete
    if (element.checkForActiveHolds())
    {
        MCRHoldCodeTrans_ds.allowDelete(false);
    }
    else
    {
        MCRHoldCodeTrans_ds.allowDelete(true);
    }
    // Evaluate the sales parameters to check if the order has
    // to be automatically submitted on clear or if the user should
    // be allowed to clear and submit on the last order.
    if (!mcrOrderParameters.MCRSubmitOnClear)
    {
        if (MCRHoldCodeTrans::getNumActiveHolds(salesTableActive.SalesId) <= 1)
        {
            ClearAndSubmit.enabled(true);
        }
        else
        {
            ClearAndSubmit.enabled(false);
        }
    }

    return ret;
}

