/// <summary>
/// Warns the user to not add systematic holds manually in the order holds for replacement order.
/// </summary>
/// <returns>Returns true when validate successfully, otherwise false.</returns>
public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (RetailNotRemoveReplacementManualOrderHolds::instance().isEnabled())
    {
        MCROrderParameters mcrOrderParametersLoc = MCROrderParameters::find();

        SalesTable salesTableReplacement;

        SalesTable salesTableLoc;

        select firstonly SalesId, ReturnItemNum from salesTableLoc
        where salesTableLoc.SalesId == MCRHoldCodeTrans.InventRefId
         join RecId from salesTableReplacement
        where salesTableReplacement.ReturnReplacementId == salesTableLoc.SalesId
        && salesTableReplacement.ReturnItemNum == salesTableLoc.ReturnItemNum;

        // If we have the return order which ReturnReplacementId matched with sales order sales id and sales order ReturnItemNum
        // The sales order is a replacement order
        if (salesTableReplacement.RecId)
        {
            if (MCRHoldCodeTrans.mcrHoldCode == mcrOrderParametersLoc.mcrFraudHoldCode
        || MCRHoldCodeTrans.mcrHoldCode == mcrOrderParametersLoc.mcrFraudManualHoldCode
        || MCRHoldCodeTrans.mcrHoldCode == mcrOrderParametersLoc.MCRPriceOverrideHoldCode
        || MCRHoldCodeTrans.mcrHoldCode == mcrOrderParametersLoc.MCRReturnCreditHold
        || MCRHoldCodeTrans.mcrHoldCode == mcrOrderParametersLoc.MCRExchangePaymBalanceHold)
            {
                if (Box::okCancel("@Retail:RetailSystematicHoldWarning", DialogButton::Cancel) == DialogButton::Cancel)
                {
                    ret = false;
                }
            }
        }
    }

    return ret;
}

