/// <summary>
///     Collects all fraud notes into one string and display them
///     on customer service screen.
/// </summary>
/// <returns>
///     The compiled fraud notes.
/// </returns>
/// <remarks>
///     To increase performance switched method to use a textBuffer.
/// </remarks>
public display notes mcrDisplayFraudNotes()
{
    DocuRef             docuRef;
    TextBuffer          tb = new TextBuffer();
    SelectableDataArea  refCompanyID = curExt();

    while select docuRef order by RecId desc
        where   docuRef.RefCompanyId == refCompanyID
            &&  docuRef.RefTableId   == tableNum(SalesTable)
            &&  docuRef.RefRecId     == SalesTable.RecId
            &&  docuRef.TypeId       == mcrOrderParameters.MCRFraudCommentTypeId

    {
        tb.appendText("@MCR28844");
        tb.appendText(docuRef.createdBy);
        tb.appendText("@MCR31247");
        tb.appendText(date2str(DateTimeUtil::date(docuRef.createdDateTime), DateFlags::FormatAll, 213, 2, 4, 2, 4, 4));
        tb.appendText("@SYS77038");
        tb.appendText(docuRef.Notes);
        tb.appendText('\n\n');
    }

    while select docuRef order by RecId desc
        where   docuRef.RefCompanyId == refCompanyID
            &&  docuRef.RefTableId == tableNum(CustTable)
            &&  docuRef.RefRecId == CustTable.RecId
            &&  docuRef.TypeId == mcrOrderParameters.MCRFraudCommentTypeId

    {
        tb.appendText("@MCR28844");
        tb.appendText(docuRef.createdBy);
        tb.appendText("@MCR31247");
        tb.appendText(date2str(DateTimeUtil::date(docuRef.createdDateTime), DateFlags::FormatAll, 213, 2, 4, 2, 4, 4));
        tb.appendText("@SYS77038");
        tb.appendText(docuRef.Notes);
        tb.appendText('\n\n');
    }

    return tb.getText();
}

