public void write()
{
    if (DocuRefPerHoldCode.RecId && !DocuRefPerHoldCode.Notes)
    {
        DocuRefPerHoldCode.delete();
        DocuRefPerHoldCode_ds.create();
    }
    else
    {
        if (DocuRefPerHoldCode.RefRecId <= 0)
        {
            if (!MCRHoldCodeTrans.RecId)
            {
                MCRHoldCodeTrans.write();
            }
            if (!MCRHoldCodeTrans.RecId)
            {
                return;
            }
            DocuRefPerHoldCode.RefRecId = MCRHoldCodeTrans.RecId;
        }

        super();
    }
}

public boolean validateWrite()
{
    boolean ret = true;
    //allow saving even though RefRecId not set. The real RefRecId will be set in write().
    if (!DocuRefPerHoldCode.RefRecId)
    {
        ret = MCRHoldCodeTrans.validateWrite();
        DocuRefPerHoldCode.RefRecId = -1;
    }

    if (!salesParameters.MCRHoldCodeNoteType)
    {
        ret = checkFailed("@MCR24327");
    }

    ret = ret && super();

    if (DocuRefPerHoldCode.RefRecId == -1)
    {
        DocuRefPerHoldCode.RefRecId = 0;
    }

    return ret;
}

/// <summary>
///     Collects all of the fraud notes into one string and display them
///     on the customer service screen.
/// </summary>
/// <returns>
///     The compiled fraud notes.
/// </returns>
/// <remarks>
///     To increase performance switched method to use a textBuffer.
/// </remarks>
public display notes mcrDisplayFraudNotes()
{
    DocuRef             docuRef;
    TextBuffer          tb = new TextBuffer();
    SelectableDataArea  refCompanyID = curExt();

    while select docuRef order by RecId desc
        where   docuRef.RefCompanyId == refCompanyID
            &&  docuRef.RefTableId == tableNum(SalesTable)
            &&  docuRef.RefRecId == RelatedSalesOrder.RecId
            &&  docuRef.TypeId == mcrOrderParameters.MCRFraudCommentTypeId

    {
        tb.appendText("@MCR28844");
        tb.appendText(' ');
        tb.appendText(docuRef.createdBy);
        tb.appendText(' ');
        tb.appendText("@MCR31247");
        tb.appendText(' ');
        tb.appendText(date2strUsr(DateTimeUtil::date(docuRef.createdDateTime), DateFlags::FormatAll));
        tb.appendText(' ');
        tb.appendText("@SYS77038");
        tb.appendText(' ');
        tb.appendText(docuRef.Notes);
        tb.appendText('\n\n');
    }

    while select docuRef order by RecId desc
        where   docuRef.RefCompanyId == refCompanyID
            &&  docuRef.RefTableId == tableNum(CustTable)
            &&  docuRef.RefRecId == RelatedCustomer.RecId
            &&  docuRef.TypeId == mcrOrderParameters.MCRFraudCommentTypeId
    {
        tb.appendText("@MCR28844");
        tb.appendText(' ');
        tb.appendText(docuRef.createdBy);
        tb.appendText(' ');
        tb.appendText("@MCR31247");
        tb.appendText(' ');
        tb.appendText(date2strUsr(DateTimeUtil::date(docuRef.createdDateTime), DateFlags::FormatAll));
        tb.appendText(' ');
        tb.appendText("@SYS77038");
        tb.appendText(' ');
        tb.appendText(docuRef.Notes);
        tb.appendText('\n\n');
    }

    return tb.getText();
}

/// <summary>
///     Collects all of the customer/Vendor notes into one string and displays them.
/// </summary>
/// <returns>
///     The compiled customer/vendor notes.
/// </returns>
/// <remarks>
///     To increase performance switched method to use a textBuffer.
/// </remarks>
public display notes mcrDisplayCustomerNotes()
{
    DocuRef             docuRef;
    TextBuffer          tb = new TextBuffer();
    SelectableDataArea  refCompanyID = curExt();

    while select docuRef order by RecId desc
        where   docuRef.RefCompanyId == refCompanyID
            &&  docuRef.RefTableId == tableNum(CustTable)
            &&  docuRef.RefRecId == RelatedCustomer.RecId
            &&  docuRef.TypeId != mcrOrderParameters.MCRFraudCommentTypeId

    {
        tb.appendText("@MCR28844");
        tb.appendText(' ');
        tb.appendText(docuRef.createdBy);
        tb.appendText(' ');
        tb.appendText("@MCR31247");
        tb.appendText(' ');
        tb.appendText(date2strUsr(DateTimeUtil::date(docuRef.createdDateTime), DateFlags::FormatAll));
        tb.appendText(' ');
        tb.appendText("@SYS77038");
        tb.appendText(' ');
        tb.appendText(docuRef.Notes);
        tb.appendText('\n\n');
    }

    return tb.getText();
}

