/// <summary>
/// Validate that the selected reason code is of the type defined in MCRReasonCodeRetailInfocodeId
/// </summary>
/// <returns>true if input is valid; otherwise, false.</returns>
public boolean validate()
{
    boolean ret = true;
    RetailInfocodeId infocodeId;
    str reasonCode;

    infocodeId = this.text();
    reasonCode = RetailMCRChannelTable::findForCurrentUser().MCRReasonCodeRetailInfocodeId;

    if (!RetailInformationSubcodeTable::exist(reasonCode, infocodeId))
    {   // Report an error - the infocodeId must be from the type reasonCode
        ret =  checkFailed(strFmt("@SYS94968", infocodeId, reasonCode));
    }

    return ret;
}

/// <summary>
/// Chooses records from related table <c>ASOHFollowingHold</c>.
/// </summary>
public void lookup()
{
    if (!ASOHFollowHoldLookupFlight::instance().isEnabled())
    {
        super();
    }

    Query query = new Query();

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(ASOHFollowingHold), this);
    sysTableLookup.addLookupField(fieldNum(ASOHFollowingHold, FollowingHold));
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(ASOHFollowingHold));

    QueryBuildRange queryBuildRange = queryBuildDataSource.addRange(fieldNum(ASOHFollowingHold, MCRHoldCode));
    queryBuildRange.value(MCRHoldCodeTrans.MCRHoldCode);

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public int selectionChange()
{
    int ret;

    ret = super();
    //Reset the filter
    if (HoldFilter.valueStr() == enum2str(MCRAllActiveClearedCheckedOut::Active))
    {
        clearedHoldCode.value(enum2str(NoYes::No));
        checkedOutStatus.value("");
    }
    else if (HoldFilter.valueStr() == enum2str(MCRAllActiveClearedCheckedOut::Cleared))
    {
        clearedHoldCode.value(enum2str(NoYes::Yes));
        checkedOutStatus.value("");
    }
    else if (HoldFilter.valueStr() == enum2str(MCRAllActiveClearedCheckedOut::CheckedOut))
    {
        clearedHoldCode.value("");
        checkedOutStatus.value(enum2str(NoYes::Yes));
    }
    else
    {
        clearedHoldCode.value("");
        checkedOutStatus.value("");
    }
    MCRHoldCodeTrans_ds.executeQuery();
    return ret;
}

public void lookup()
{
    RetailMCRChannelTable::lookupReasonCodes(this);
}
