[Form]
public class MCRItemDisplay extends FormRun
{
    InventTransId       inventTransId;
    ItemId              itemId;

}

/// <summary>
///     Displays all appropriate messages for this item in the message window.
/// </summary>
/// <param name="_category">
///     The <c>MCRMessageCategoryID</c> to display messages for.
/// </param>
void displayItemMessage(MCRMessageCategoryID _category)
{
    str message;

    message = MCRMessageAssociation::getFullMessage(inventTable.TableId,
                                    inventtable.RecId,
                                    MCRMessageTiming::Begin,
                                    _category);

    messageWindow.text(message);
}

public void init()
{
    EcoResProductRecId  productId;
    container categories;
    int categoriesConLoop = 1;

    super();

    // Doesn't technically need a caller, but needs to allow saved and unsaved
    // sales lines, and must not be opened from the AOT
    if (!element.args().caller())
    {
        throw error("@SYS22539");
    }

    // Load the sales table and item table
    salesLine.data(element.args().record());

    InventTable inventTableRecord = InventTable::find(salesLine.ItemId);
    inventTable.data(inventTableRecord); // correct usage of .data - not trying to write this data to the database
    if (inventTableRecord)
    {
        element.args().lookupRecord(inventTableRecord);
        inventTable_ds.research(false);
    }

    inventTransId = salesLine.InventTransId;
    itemId = salesLine.ItemId;
    productId = inventTableRecord.Product;

    // Load ratings and reviews of the product
    int totalRatingValue = RetailProductRating::getTotalNumberOfRatings(productId);
    real averageRatingValue = RetailProductRating::getAverageRating(productId);
    productRating.rating(averageRatingValue);
    productRating.totalRating(totalRatingValue);

    // Determine which message categories have messages for this item,
    // and loads the box appropriately.
    categories = MCRMessageAssociation::getCategoriesOnMessages(inventTable);
    messageCategoryBox.enabled(false);
    while (categoriesConLoop <= conLen(categories))
    {
        messageCategoryBox.enabled(true);
        messageCategoryBox.add(conPeek(categories, categoriesConLoop));
        categoriesConLoop++;
    }

    element.displayItemMessage(messageCategoryBox.valueStr());

    // If there are no cross-sell items, disable the cross-sell button
    upSellButton.enabled(MCRUpSellItem::existItem(itemId));

    // Most buttons should be disabled if the sales line passed in hasn't
    // been saved yet.
    if (!salesLine)
    {
        upsellButton.enabled(false);
        markUpTrans.enabled(false);
    }
}

public void run()
{
    super();
    if (messageCategoryBox.enabled())
    {
        // Sets the focus on the message category box if messages are available.
        messageCategoryBox.setFocus();
    }
    else
    {
        // Otherwise, set it to the first button in the buttongroup.
        buttonGroup.setFocus();
    }
}

public void executeQuery()
{
    QueryBuildRange queryBuildRangeItemRange;

    // This ensures the item on the sales line and the buffer have the same data.
    // This was added because sometimes the data source was not being passed
    // correctly into other forms.
    queryBuildRangeItemRange = this.queryBuildDataSource().addRange(fieldNum(InventTable, ItemId));
    queryBuildRangeItemRange.value(itemId);

    super();
}

public void executeQuery()
{
    QueryBuildRange purchLineStatus;

    // Ensures only open purchase orders are viewable.
    purchLineStatus = this.queryBuildDataSource().addRange(fieldNum(PurchLine, PurchStatus));
    purchLineStatus.value(enum2str(PurchStatus::Backorder));

    super();
}

public void executeQuery()
{
    QueryBuildRange             salesLineRange;

    // This ensures the sales line passed and the buffer have the same data.
    // This was added because sometimes the data source was not being passed
    // correctly into other forms.
    salesLineRange = this.queryBuildDataSource().addRange(fieldNum(SalesLine, InventTransId));
    salesLineRange.value(inventTransId);

    super();
}

void clicked()
{
    super();

    // Opens the appropriate Up-sell/Cross-sell order screen.
    TmpMCRUpSellOrderEntry::mcrJumpRef(salesLine);
}

public boolean modified()
{
    boolean ret;

    ret = super();
    // Changes the message displayed depending on the message category.
    element.displayItemMessage(this.valueStr());

    return ret;
}
