[Form]
public class MCRItemListCopying extends FormRun
{
    CustAccount             custAccountCalling;
    TmpFrmVirtual           tmpFrmVirtual;
    MCRInventDimCtrl_Frm_ItemList inventDimFormSetup;

    Map                     origValues;
    Map                     origInventDim;
    MapEnumerator           origValueEnumerator;
    MapEnumerator           origInventDimEnumerator;
    boolean                 noLinesCreated;

}

public boolean canClose()
{
    boolean ret = true;

    element.revertInventDim();
    element.revertQuantities();

    if (noLinesCreated)
    {
        if (Box::yesNo("@MCR4410256", DialogButton::No) == DialogButton::No)
        {
            ret = false;
        }
    }

    ret = ret && super();

    return ret;
}

public void clearAll()
{
    MCRItemListLine_ds.markAllLoadedRecords(0);
}

/// <summary>
/// Creates the sales lines and refreshes the grid.
/// </summary>
/// <param name="_salesTable">
/// The record in the <c>SalesTable</c> to create lines for.
/// </param>
void  createSalesLine(SalesTable _salesTable)
{
    tmpFrmVirtual.setTmpData(salesLine::createSalesLine(_salesTable,
                                                        tmpFrmVirtual,
                                                        ModuleInventPurchSales::Sales));
    MCRItemListLine_ds.executeQuery();
}

/// <summary>
/// Creates the sales order lines and refreshes the calling sales line grid.
/// </summary>
void  copyLines()
{
    Object          callerDataSource;
    Common          callerRecord;
    TableID         callerTableID;
    MCRItemListLine mcrItemListLineLoc;

    callerTableID = element.args().dataset();

    tmpFrmVirtual.clear();
    for (mcrItemListLineLoc = MCRItemListLine_ds.getFirst(1);
        mcrItemListLineLoc;
        mcrItemListLineLoc = MCRItemListLine_ds.getNext())
    {
        tmpFrmVirtual.initFromMCRItemListLine(mcrItemListLineLoc);

        tmpFrmVirtual.insert();
    }

    if (tmpFrmVirtual)
    {
        callerRecord = element.args().record();
        if (this.createSalesLinesFromCaller(callerRecord))
        {
            callerDataSource = FormDataUtil::getFormDataSource(callerRecord);

            if (formDataSourceHasMethod(callerDataSource, identifierStr(reReadLines)))
            {
                callerDataSource.reReadLines();
            }
        }

        noLinesCreated = false;
    }
    else
    {
        noLinesCreated = true;
    }
}

protected boolean createSalesLinesFromCaller(Common _caller)
{ 
    boolean ret = false; 

    if (_caller.TableId == tableNum(SalesTable)) 
    { 
        SalesTable salesTable = _caller;

        if (salesTable.checkUpdate()) 
        { 
            element.createSalesLine(salesTable); 
            ret = true;
        }
    }

    return ret; 
}

/// <summary>
/// Initializes the form and validates that the form is called with a <c>SalesTable</c> buffer.
/// </summary>
public void init()
{
    SalesTable salesTable;

    if (! element.args().dataset())
        throw error(strFmt("@SYS22678",element.name()));

    if (element.args().dataset() == tableNum(SalesTable))
    {
        salesTable = element.args().record();
        this.initFromCaller(salesTable);
    }

    super();

    //Initialize splitter
    element.updateDesign(InventDimFormDesignUpdate::Init);

    origValues    = new Map(Types::Int64, Types::Real);
    origInventDim = new Map(Types::Int64, Types::String);
}

protected void initFromCaller(SalesTable _salesTable)
{
    custAccountCalling = _salesTable.CustAccount;
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

protected void revertQuantities()
{
    MCRItemListLine mcrItemListLineLocal;

    // Reset sales qty values if any were changed,
    if (!origValues.empty())
    {
        // and if the user wants to.
        if (Box::yesNo("@MCR4410220", DialogButton::Yes) == DialogButton::No)
        {
            origValueEnumerator = new MapEnumerator(origValues);

            ttsBegin;
            while (origValueEnumerator.moveNext())
            {
                select firstOnly forUpdate mcrItemListLineLocal
                    where mcrItemListLineLocal.RecId == origValueEnumerator.currentKey();

                mcrItemListLineLocal.SalesQty = origValueEnumerator.currentValue();
                mcrItemListLineLocal.update();
            }
            ttsCommit;
        }
    }
}

protected void revertInventDim()
{
    MCRItemListLine mcrItemListLineLocal;

    // Reset inventory dimension values if any were changed,
    if (!origInventDim.empty())
    {
        origInventDimEnumerator = new MapEnumerator(origInventDim);

        ttsbegin;
        while (origInventDimEnumerator.moveNext())
        {
            select firstonly forupdate mcrItemListLineLocal
                where mcrItemListLineLocal.RecId == origInventDimEnumerator.currentKey();

            mcrItemListLineLocal.InventDimId = origInventDimEnumerator.currentValue();
            mcrItemListLineLocal.update();
        }
        ttscommit;
    }
}

public void selectAll()
{
    MCRItemListLine_ds.markAllLoadedRecords(1);
}

public void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = MCRInventDimCtrl_Frm_ItemList::newFromForm(element);
            }
        case InventDimFormDesignUpdate::Active:
        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(MCRItemListLine.ItemID));
            inventDimFormSetup.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Sets the query values to filter on all generic item lists as well as the customer specific one.
/// </summary>
public void init()
{
    QueryBuildDataSource  queryBuildDataSource;
    str                   criteriaStr;

    super();

    queryBuildDataSource = this.query().dataSourceTable(TableNum(MCRItemListTable));
    criteriaStr = strfmt('(CustAccount == "%1") || (CustAccount == "")', custAccountCalling);

    queryBuildDataSource.addRange(fieldNum(MCRItemListTable, CustAccount)).value(criteriaStr);
}

public void executeQuery()
{
    super();
    element.selectAll();
}

