    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public boolean validateWrite()
{
    boolean ret = true;

    // Validate item dimensions if any are populated.
    if (InventDim.validateWriteProductDimensions(MCRItemListLine.ItemId, true))
    {
        // Dimensions are valid or none are populated then create/find the InventDim
        MCRItemListLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
    }
    else
    {
        InventDim_ds.reread();
        ret = false;
    }

    ret = super() && ret;
    return ret;
}

public void write()
{
    super();

    if (MCRItemListLine.InventDimId != inventDim.InventDimId)
    {
        // Reload the inventDim record after the write since a new one may be created.
        inventDim.data(InventDim::find(MCRItemListLine.InventDimId));
        inventDim_ds.setCurrent();
    }
}

public void write()
{
    // Don't write - instead this is managed for doing
    // a find or create for the inv dimensions and then
    // manually updating the datasource through code.
}

