public boolean modified()
{
    boolean ret;
    // save values off to map for update later.
    if (!origValues.exists(MCRItemListLine.RecId))
    {
        origValues.insert(MCRItemListLine.RecId, MCRItemListLine.SalesQty);
    }

    ret = super();
    // check that the user is not resetting to the original value.
    // if so, no need to update, thus remove the element from the map.
    if (origValues.lookup(MCRItemListLine.RecId) == MCRItemListLine.SalesQty)
    {
        origValues.remove(MCRItemListLine.RecId);
    }

    return ret;
}

