    }

    return formRunHelper;
}

public void init()
{
    QueryBuildDataSource source;

    boolean enableConfigurationFeature = PCRuntimeLibrary::isConfigurationKeyEnabled();

    RetailParameters retailParameters;

    #ISOCountryRegionCodes

    // If not called from customer service form's order details button
    if (element.args().menuItemName() != menuitemDisplayStr(MCROrderDetails))
    {
        throw error("@Retail:FormHasNotBeenCalledFromCustomerServiceScreen");
    }

    custParameters = CustParameters::find();
    returnTableButtonStateProvider = new ReturnTableButtonStateProvider();

    isIsoMY = TaxGSTFeatureChecker_MY::isCountryRegionMY();
    if (isIsoMY)
    {
        custInvoiceJourLookupController = CustInvoiceJourLookupController_MY::construct();
        isWrnPrintedMY                  = false;
    }

    isIN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    if (isIN)
    {
        isIndiaIndirectTaxParameterMarked = TaxParameters::checkTaxParameters_IN();
        isSalesLine_INEnabled             = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(SalesLine_IN));
    }

    isRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);

    super();

    salesTableSource = element.args().record();

    if (salesTableSource.RecId)
    {
        this.ChangeCust(salesTableSource.CustAccount);

        source = SalesTable_ds.query().dataSourceTable(tableNum(SalesTable));
        source.clearRange(fieldNum(SalesTable, SalesId));
        source.addRange(fieldNum(SalesTable, SalesId)).value(salesTableSource.salesid);

        SalesTable_ds.executeQuery();
    }

    if (salesTableSource.SalesType == SalesType::ReturnItem)
    {
        salesTableForm_return = SalesTableForm::construct(SalesTableFormId::ReturnOrder, element.args().record());

        salesTableForm_return.parmFormDataSource(salesTable_ds);
        salesTableForm_return.parmArgs(element.args());

        salesTable_ds.object(fieldNum(SalesTable, SalesType)).allowEdit(salesTableForm_return.editSalesType());

        salesTable_ds.query(salesTableForm_return.querySalesTable(salesTable_ds.query()));
        salesLine_ds.query(salesTableForm_return.querySalesLine(salesLine_ds.query()));
    }
    else
    {
        salesTableForm = SalesTableForm::construct(SalesTableFormId::None, element.args().record());

        salesTable_ds.object(fieldNum(SalesTable, SalesType)).allowEdit(salesTableForm.editSalesType());

        salesTable_ds.query(salesTableForm.querySalesTable(salesTable_ds.query()));
        salesLine_ds.query(salesTableForm.querySalesLine(salesLine_ds.query()));
    }

    if (!TaxParameters::find().CashDiscOnInvoice)
    {
        salesTable_ds.object(fieldNum(SalesTable, CashDiscPercent)).allowEdit(false);
    }

    //Show fasttab summary
    salesTable_ds.autoSearch(true);

    mcrOrderParameters = MCROrderParameters::find();
    salesParameters    = SalesParameters::find();
    mcrCallCenterEnabled = MCROrderParameters::isCallCenterEnabledAndInUse();

    if (mcrCallCenterEnabled)
    {
        retailParameters = RetailParameters::find();
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
    salesLineGrid.visible(true);

    onInit = true;

    mcrMarginAlert.visible(salesParameters.mcrEnableMarginAlert);
    mcrMarginPercentStr.visible(salesParameters.mcrEnableMarginAlert);
    marginMap = new Map(Types::String, Types::Container);
    mcrPriceHistory.visible(custParameters.mcrEnablePriceDetails);

    imagelist = new Imagelist(Imagelist::iconWidth(), Imagelist::smallIconHeight());
    shipToIcon_SO.imageList(imagelist);
    copyOfShipToIcon_SO.imageList(imagelist);

    CustDirectDebitMandate::defaultAndDisplayMandate(
        salesTable.InvoiceAccount,
        salesTable.PaymMode,
        salesTable.DirectDebitMandate,
        false,
        salesTable_ds,
        fieldNum(SalesTable, DirectDebitMandate));

    if (salesTableSource.SalesType == SalesType::ReturnItem)
    {
        LineViewHeader.caption("@SYS340851");
        TabPageLines.caption("@GLS180009");
        GroupSalesLine.visible(false);
        GroupReturnLine.visible(true);
        TabPageReturns.visible(false);
    }
    else
    {
        LineViewHeader.caption("@SYS335042");
        TabPageLines.caption("@SYS335043");
        GroupSalesLine.visible(true);
        GroupReturnLine.visible(false);
        TabPageReturns.visible(true);
    }
}

