void mcrReturnInquiry()
{
    FormRun                 formRun;
    MCRReturnTableConfigure returnTableConfigure = new MCRReturnTableConfigure();
    Args                    formArgs = new Args();

    returnTableConfigure.parmCustAccount(custTable.AccountNum);

    formArgs.parmObject(returnTableConfigure);
    formArgs.caller(this);

    formRun = new MenuFunction(menuItemDisplayStr(ReturnTableDetails), MenuItemType::Display).create(formArgs);

    formRun.run();
    formRun.wait();
}

void mcrReturnInquiry_Order(boolean isCallerReturn = true)
{
    FormRun                 formRun;
    Args                    formArgs = new Args();
    MCRReturnTableConfigure returnTableConfigure = new MCRReturnTableConfigure();

    returnTableConfigure.parmCustAccount(salesTableReturn.CustAccount);

    if (isCallerReturn && (salesTableReturn.SalesType == SalesType::ReturnItem))
    {
        returnTableConfigure.parmSalesId(salesTableReturn.SalesId);
    }
    else
    {
        returnTableConfigure.parmOriginalSalesId(salesTableReplacement.SalesId);
    }

    formArgs.parmObject(returnTableConfigure);
    formArgs.caller(this);

    formRun = new MenuFunction(menuItemDisplayStr(ReturnTableDetails), MenuItemType::Display).create(formArgs);

    formRun.run();
    formRun.wait();
}

public void mcrSetHeaderControls()
{
    boolean allowEdit   = true;
    boolean allowDelete = true;
    SalesTableInteractionHelper salesTableInteractionHelper;

    if (enableDirectedSelling || enableOrderCompletion)
    {
        salesTableInteractionHelper = SalesTableInteractionHelper::construct();
        salesTableInteractionHelper.mcrInitHeaderControls(salesTable,
                                                            enableOrderCompletion,
                                                            returnProcessing,
                                                            mcrReturnSalesTable.IsExchange);
    }

    if (enableOrderCompletion)
    {
        allowEdit   = salesTableInteractionHelper.parmMCRHeaderAllowEdit();
        allowDelete = salesTableInteractionHelper.parmMCRHeaderAllowDelete();

        lineTaxTransSource.enabled(allowEdit);
    }
}

