// <GIN>
void setTaxWithholdEnabled_IN()
{
    salesTable_ds.object(fieldNum(SalesTable, tcsGroup_IN)).enabled(!salesTable.tdsGroup_IN);
    salesTable_ds.object(fieldNum(SalesTable, tdsGroup_IN)).enabled(!salesTable.tcsGroup_IN);
    salesLine_IN_ds.object(fieldNum(SalesLine_IN, tcsGroup)).enabled(!salesLine_IN.tdsGroup);
    salesLine_IN_ds.object(fieldNum(SalesLine_IN, tdsGroup)).enabled(!salesLine_IN.tcsGroup);

    if (salesLine.InventTransIdReturn)
    {
        salesTable_ds.object(fieldNum(SalesTable, tcsGroup_IN)).enabled(!salesLine.InventTransIdReturn);
        salesTable_ds.object(fieldNum(SalesTable, tdsGroup_IN)).enabled(!salesLine.InventTransIdReturn);
        salesLine_IN_ds.object(fieldNum(SalesLine_IN, tcsGroup)).enabled(!salesLine.InventTransIdReturn);
        salesLine_IN_ds.object(fieldNum(SalesLine_IN, tdsGroup)).enabled(!salesLine.InventTransIdReturn);
    }
}

// </GIN>
void updateCustControls()
{
    boolean custExists = custTable.RecId != 0;
    boolean saleExists = !(salesTable.SalesId);
    boolean enableCustButtons;

    // Enable customer buttons only if customer is active.
    enableCustButtons = (custExists);
    customerDetails.enabled(enableCustButtons);
    customerOptions.enabled(enableCustButtons);
    generalCustomerCases.enabled(enableCustButtons);

    // If customer is blocked for all, do not enable the create order button.
    if (CustTable::blocked(custTable.AccountNum) == CustVendorBlocked::All)
    {
        createOrder.enabled(false);
    }
    else
    {
        createOrder.enabled(custExists);
    }

    busRelTable.visible(smmBusRelTable.RecId == 0 ? false: true);
    salesQuotationNavigateCtrl.visible(smmBusRelTable.RecId == 0 ? false: true);
    smmCampaignTable.visible(smmBusRelTable.RecId == 0 ? false: true);
    smmTMCallListGroup.visible(smmBusRelTable.RecId == 0 ? false: true);
    smmActivities.visible(smmBusRelTable.RecId == 0 ? false: true);
}

/// <summary>
/// Updates the related order and related invoice parts.
/// </summary>
void updateRelatedOrderControls()
{
    if (salesTable.SalesId)
    {
        mcrSalesRelatedOrders.enabled(salesTable.SalesId ? true: false);
    }
}

/// <summary>
/// update the controls on the form.
/// </summary>
void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(salesLine);
    Set            validInventDimDSset;
    InventDimControlIdCollection inventDimControlIdCollection;
    boolean emptyLine;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                validInventDimDSset = new Set(Types::String);
                validInventDimDSset.add(inventDim_ds.name());
                inventDimControlIdCollection = InventDimControlIdCollection::construct();
                inventDimControlIdCollection.add(inventoryDimensionsGrid.id(), true);
                inventDimFormSetup = InventDimCtrl_Frm_Mov_OrderLine::newFromFormMovement(element, movement, validInventDimDSset, inventDimControlIdCollection);
            }
            break;

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange:
             emptyLine = salesLine.ItemId == '' && salesLine.SalesCategory == 0;
             inventoryDimensionsGrid.allowEdit(!emptyLine);
             if (!emptyLine)
             {
                 //For performance reasons, only call the inventDimFormSetup if the order line has an item ID or Category ID.
                inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
                inventDimFormSetup.formSetControls(mode == InventDimFormDesignUpdate::Active);
             }
             break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Updates product dimensions and variant number on the sales line.
/// </summary>
/// <param name="_product">
/// The product containing new dimensions.
/// </param>
private void updateProductDimensions(InventDistinctProduct _product)
{
    // Update the variant id (Retail)
    var variantId = InventDimCombination::findByDistinctProductVariant(_product.Product).RetailVariantId;
    if (variantId != SalesLine_RBOVariantId.valueStr())
    {
        salesLine.RetailVariantId = variantId;
    }

    Enumerator enum = InventProductDimension::getEnumeratorForEnabledDimensions();
    while (enum.moveNext())
    {
        InventProductDimension productDim = enum.current() as InventProductDimension;
        FieldId productFieldId = InventDistinctProduct::productDimensionFieldId(productDim);
        if (productFieldId)
        {
            FieldId inventDimFieldId = productDim.parmInventDimFieldId();
            inventDim.(inventDimFieldId) = _product.(productFieldId);
        }
    }

    InventDim newInventDim = InventDim::findOrCreate(inventDim);
    if (newInventDim.InventDimId != SalesLine.InventDimId)
    {
        salesLine.InventDimId = newInventDim.InventDimId;
        salesLine.initFromProductDimensions(inventDim);
        salesLine.setPriceDisc(inventDim);
        salesLine_DS.cacheCalculateMethod(tableMethodStr(SalesLine, itemName));
    }
}

public void init()
{
    super();

    // Cache appropriate display methods.
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayLastShipDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrOrderReleaseDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayDeliveryName));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayTender));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrHasExpeditedLines));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrOrderReleaseDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrGetPaymStatus));
}

/// <summary>
/// Displays the Route associated with the Sales order, if applicable.
/// </summary>
/// <returns>
/// The Route code if there is only one associated with the order, otherwise an empty string or "Multiple".
/// </returns>
public display TMSRouteCode tmsDisplayRouteCode()
{
    if (salesTableSource.SalesType == SalesType::ReturnItem)
    {
        return salesTableForm_Return.tmsDisplayRouteCode(salesTable);
    }
    else
    {
        return salesTableForm.tmsDisplayRouteCode(salesTable);
    }
}

display OrderType getOrderType(SalesTable _salesTable)
{
    OrderType orderType = OrderType::Sales;

    if (_salesTable.SalesType == SalesType::ReturnItem)
    {
        orderType = OrderType::Return;
    }

    return orderType;
}

public int active()
{
    int ret;

    ret = super();

    mcrCallCenterOrder = salesTable.mcrIsCallCenter();

    element.updateRelatedOrderControls();

    return ret;
}

/// <summary>
/// Gets the defined ship to customer icon.
/// </summary>
/// <param name="_salesTable">
