/// </param>
/// <returns>
/// The defined ship to customer icon.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display container mcrDisplayShipToCustomer(SalesTable _salesTable)
{
    if (_salesTable.CustAccount != custTable.AccountNum)
    {
        return ImageReference::constructForAotResource(resourceStr(MCRShipTo)).pack();
    }

    return connull();
}

void setTouched()
{
    // Required by form MarkupTrans to communicate changes to the caller
    salesTableForm.setTouched(salesTable);
}

public void jumpRef()
{
    Args    args;
    FormRun FR;

    // If this is a return order, load the return form;
    // otherwise execute default logic to laod sales order form.
    if (salesTable.SalesType == SalesType::ReturnItem)
    {
        args = new Args();
        args.record(salesTable);

        FR = new MenuFunction(menuitemDisplayStr(ReturnTableDetails), MenuItemType::Display).create(args);
        FR.run();
        FR.wait();
    }
    else
    {
        super();
    }
}

public int active()
{
    int         ret;
    boolean     canHavePriceHistory;

    ret = super();

    // Enable or disable price history.
    canHavePriceHistory = true;
    if (salesLine.LineDeliveryType == LineDeliveryType::DeliveryLine
        || mcrSalesTable.ContinuityChild)
    {
        canHavePriceHistory = false;
    }

    element.updateDesign(InventDimFormDesignUpdate::Active);
    onInit = true;

    if (salesTableSource.SalesType == SalesType::Sales)
    {
        mInventTransIdRange.value(queryValue(salesLine.InventTransId));
        salesLineReturnOrder_ds.executeQuery();

        if (!salesTableReturn.ReturnItemNum)
        {
            mReturnItemNumRange.value(queryValue("1 == 2"));
            salesTableReplacement_ds.executeQuery();
        }
    }

    return ret;
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    SalesLine           salesLineLocal = _record;
    int                 priceOverrideLineColor = mcrOrderParameters.mcrPriceOverrideLineColor;

    if (priceOverrideLineColor
        && salesLineLocal.mcrSalesLinePriceOverride().PriceOverride == NoYes::Yes
        && salesLineLocal.LineDeliveryType != LineDeliveryType::DeliveryLine)
    {
        // Show the display color for override price
        _options.backColor(priceOverrideLineColor);
    }
    else
    {
        _options.clearBackColor();
    }
    super(_record, _options);
}

public void executeQuery()
{
    QueryBuildDataSource source;

    // Get the query datasource and add the range.
    source = this.query().dataSourceTable(tableNum(SalesLine));
    source.clearRange(fieldNum(SalesLine, LineDeliveryType));
    // Show multiple delivery address header lines.
    source.addRange(fieldNum(SalesLine, LineDeliveryType)).value('!' + enum2str(LineDeliveryType::OrderLineWithMultipleDeliveries));

    super();
}

public void init()
{
    super();

    //Sort lines by line number.
    this.query().dataSourceTable(tableNum(SalesLine)).addSortField(
        fieldNum(SalesLine, LineNum));

    // Cache appropriate display methods.
    this.cacheAddMethod(tableMethodStr(SalesLine, itemName));
    this.cacheAddMethod(tableMethodStr(SalesLine, mcrDeliverPickedInTotalSalesUnit));
    this.cacheAddMethod(tableMethodStr(SalesLine, deliveredInTotal));
    this.cacheAddMethod(tableMethodStr(SalesLine, mcrReturnedInventInTotal));
}

