display container lineDeliveryTypeImage(SalesLine _salesLine)
{
    switch (_salesLine.LineDeliveryType)
    {
        case LineDeliveryType::OrderLineWithMultipleDeliveries:
            return OrderLineMultipleDeliveriesImageData;
        case LineDeliveryType::DeliveryLine:
            return DeliveryLineImageData;
        default:
            return connull();
    }
}

/// <summary>
/// Returns the margin percent value and margin icon for the sale line.
/// </summary>
/// <param name="_salesLine">
/// A buffer of the sales line record selected on the form.
/// </param>
/// <returns>
/// The margin percent and icon.
/// </returns>
private container mcrGetMarginValues(SalesLine _salesLine)
{
    MCRMarginPecentStr  marginPecentStr;

    if (salesParameters.mcrEnableMarginAlert
        && _salesLine.InventTransId)
    {
        // Use the value in the map for performance reasons if exists
        if (marginMap.exists(_salesLine.InventTransId))
        {
            return marginMap.lookup(_salesLine.InventTransId);
        }
        else
        {
            str      mcrIcon;
            marginPecentStr = _salesLine.mcrDisplayMarginPercentStr();

            // If the margin could not be calculated 'N/A' will be returned so don't
            // determine the icon
            if (marginPecentStr != "@SYS180118")
            {
                mcrIcon = MCRMarginAlert::getMarginSymbol(str2num(marginPecentStr));
            }
            marginMap.insert(_salesLine.InventTransId, [marginPecentStr, mcrIcon]);

            return [marginPecentStr, mcrIcon];
        }
    }

    return conNull();
}

public void executeQuery()
{
    QueryBuildDataSource qbdsBusRelTable;
    qbdsBusRelTable = this.query().dataSourceName(identifierStr(smmBusRelTable));

    // Ensure that no smmBusRelTable record gets selected when the form first starts. and runs this execute query.
    // Only After a user has searched for a customer should this datasource join to the customer record
    // and get any related business relation.
    qbdsBusRelTable.addRange(fieldNum(smmBusRelTable, Party)).value(SysQuery::valueNotEmptyString());

    super();
}

/// <summary>
/// Collects all customer notes into one string.
/// </summary>
/// <returns>
/// A <c>Notes</c> string containing the customer notes.
/// </returns>
public Notes mcrGetCustomerNotes()
{
    DocuRef                 docuRef;
    TextBuffer              custNotesTextBuffer = new TextBuffer();
    SelectableDataArea      refCompanyID = curext();
    TransDate               dateCreated;

    while select docuRef order by RecId desc
        where   docuRef.RefCompanyId == refCompanyID
            &&  docuRef.RefTableId == tableNum(CustTable)
            &&  docuRef.RefRecId == custTable.RecId
    {
        if (docuRef.Notes != '')
        {
            // Build the line using a STRFMT function instead of individual labels.
            // Apply time zone offset to docuref
            dateCreated = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(docuRef.CreatedDateTime, DateTimeUtil::getCompanyTimeZone()));
            custNotesTextBuffer.appendText(strFmt("@MCR31836", docuRef.CreatedBy,
                    date2StrUsr(dateCreated, DateFlags::FormatAll), docuRef.Notes));
            custNotesTextBuffer.appendText('\n');
        }
    }
    return custNotesTextBuffer.getText();
}

public int active()
{
    int ret;
    CustVendorBlocked blockLevel = CustTable::blocked(custTable.AccountNum);
    ret = super();

    // Pop up hot alerts for customer issues if they exist.
    element.mcrHotAlertMsg();

    // Check if the customer has a stop placed on them.
    if (blockLevel == CustVendorBlocked::Invoice)
    {
        warning(strFmt("@SYS18389", custTable.AccountNum, enum2str(blockLevel)));
    }
    else if (blockLevel == CustVendorBlocked::All)
    {
        warning(strFmt("@SYS18389", custTable.AccountNum, enum2str(blockLevel)));
    }

    return ret;
}

public void executeQuery()
{
    QueryBuildDataSource source;
    str searchString;

    // Get the query datasource.
    source = this.query().dataSourceTable(tableNum(CustTable));

    source.clearRange(fieldNum(CustTable, AccountNum));

    // Search only for the specified customer.
    if (chosenCustomer)
    {
        searchString = chosenCustomer;

        // Re-initalize chosen customer after utilizing.
        chosenCustomer = '';
    }
    else
    {
        searchString = '..';
    }
    source.addRange(fieldNum(CustTable, AccountNum)).value(searchString);
    super();

    // Run the execute query method to ensure that the data is up to date
    // before running the updateCustControls() method called below.
    smmBusRelTable_ds.executeQuery();

    // Update the properties of the customer controls.
    element.updateCustControls();
}

public void init()
{
    Query query = new Query();

    super();
    query.addDataSource(tableNum(CustTable));
    this.query(query);

    // Caches the customer display methods.
    this.cacheAddMethod(tableMethodStr(CustTable, name));
    this.cacheAddMethod(tableMethodStr(CustTable, email));
    this.cacheAddMethod(tableMethodStr(CustTable, address));
    this.cacheAddMethod(tableMethodStr(CustTable, phone));
    this.cacheAddMethod(tableMethodStr(CustTable, phoneLocal));
}

/// <summary>
/// Refreshes the record when the form has been opened by another form
/// and the user has selected a different record in the calling form.
/// </summary>
public void linkActive()
{
    CustTable   custTableLocal;

    if (element.args().caller() && element.args().dataset() == tableNum(CustTable))
    {
        custTableLocal = element.args().record();
        custTable_ds.query().dataSourceName(custTable_ds.name()).clearDynalinks();

        // Set the chosenCustomer, used when coming from the Customer form to open
        // customer service for a chosen customer.
        chosenCustomer = custTableLocal.AccountNum;
    }

    super();
}

/// <summary>
/// Validates to ensure that a phantom customer account cannot be manually entered.
/// </summary>
/// <returns>
/// true if the customer account has not been merged; otherwise, false.
/// </returns>
public boolean validate()
{
    boolean ret;

    ret = super();

    if (custTable.mcrMergedParent)
    {
        ret = checkFailed(strFmt("@MCR12337", custTable.AccountNum, custTable.mcrMergedParent));
    }

    return ret;
}

public void write()
{
    if (salesLine_W.SalesLine)
    {
        // no-op because a record with an empty FK is not valid
        super();

        // <GMY>
        if (isIsoMY)
        {
            if (SalesTable.hasLinesWithRefrence_MY() && SalesTable.hasLinesWithoutRefrence_MY())
            {
                if (!isWrnPrintedMY)
                {
                    warning("@SYP4860678");
                    isWrnPrintedMY = true;
                }
            }
            else
            {
                isWrnPrintedMY = false;
            }
        }
        // <GMY>
    }
}

public void init()
{
    isSalesLineWEnabled = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(SalesLine_W));

    if (isSalesLineWEnabled)
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();

    if (isSalesLineWEnabled)
    {
        element.formRunHelper().addExtensionTablePair(salesLine, salesLine_W);
    }
}

public boolean validateWrite()
{
    boolean         ret;

    if (SalesLine_W.SalesLine)
    {
        ret = super();
    }
    else
    {
        ret = true;
    }

    // <GMY>
    if (ret && isIsoMY)
    {
        if (SalesLine_W.RefInvoiceId_MY && SalesLine_W.RefInvoiceDate_MY)
        {
            if (!CustInvoiceJour::existByInvoiceIdTransDate(SalesLine_W.RefInvoiceId_MY, SalesLine_W.RefInvoiceDate_MY))
            {
                ret = checkFailed("@SYP4860680");
            }
        }
        else if ((SalesLine_W.RefInvoiceId_MY && !SalesLine_W.RefInvoiceDate_MY) || (!SalesLine_W.RefInvoiceId_MY && SalesLine_W.RefInvoiceDate_MY))
        {
            ret = checkFailed("@SYP4860689");
        }
    }
    // <GMY>

    return ret;
}

