{
    boolean ret;

    if (whsSalesLine.RecId)
    {
        ret = super();
    }
    else
    {
        ret = true;
    }

    return ret;
}

public void selectionChanged()
{
    boolean                 enableButton;
    SalesTable              salesTableRet;

    super();

    salesTableRet = SalesTable::find(saleslinereturnorder.SalesId);

    btnReturnInvoice.enabled(!salesTableRet.ProjId && salesTableRet.lastInvoice() != '');

    enableButton = RetailMCRChannelTable::mcrEnableOrderCompletion(salesTableRet)
                    && salesTableRet.type().mcrAllowPaymentAccess();
    btnReturnPayments.enabled(enableButton);
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(SalesLine));
    qbds.clearDynalinks();
    qbds.addRange(fieldNum(SalesLine, SalesType)).value(queryValue(SalesType::ReturnItem));
    mInventTransIdRange = qbds.addRange(fieldNum(SalesLine, InventTransIdReturn));
}

