    salesTableForm.setTouched(salesTableReturn);
}

/// <summary>
/// Gets the defined ship to customer icon.
/// </summary>
/// <param name="_salesTable">
/// The <c>SalesTable</c> record for which the customer is a ship-to customer.
/// </param>
/// <returns>
/// The defined ship to customer icon.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display container mcrDisplayShipToCustomer(SalesTable _salesTable)
{
    if (salesTableReturn.CustAccount != custTable.AccountNum)
    {
        return ImageReference::constructForAotResource(resourceStr(MCRShipTo)).pack();
    }

    return connull();
}

public int active()
{
    int ret;
    CustInvoiceTrans custInvoiceTrans;

    ret = super();

    if ((salesTableSource.SalesType == SalesType::Sales) && (!SalesLine.RecId))
    {
        mInventTransIdRange.value(queryValue(" 1 == 2"));
        salesLineReturnOrder_ds.executeQuery();
        salesTableReturn_ds.executeQuery();
        mcrSalesTableReturn_ds.executeQuery();
    }

    // Check if return options should be allowed and if not then disable the corresponding buttons.
    if (salesTableReturn.SalesType == SalesType::ReturnItem
        || salesTableReturn.mcrReturnSalesTable().IsExchange
        || salesTableReturn.mcrHasReturn())
    {
        orderReturnInquiry.enabled(true);
    }
    else
    {
        orderReturnInquiry.enabled(false);
    }

    if (salesTableReturn.SalesType != SalesType::ReturnItem)
    {
        select firstonly RecId from custInvoiceTrans
            where custInvoiceTrans.SalesId == salesTableReturn.SalesId;
    }

    if (custInvoiceTrans.RecId)
    {
        soCreateReturn.enabled(true);
    }
    else
    {
        soCreateReturn.enabled(false);
    }

    element.updateRelatedOrderControls();

    if (salesTableSource.SalesType == SalesType::ReturnItem)
    {
        mReturnItemNumRange.value(queryValue(salesTableSource.ReturnItemNum));
    }
    else
    {
        mReturnItemNumRange.value(queryValue(salesTableReturn.ReturnItemNum));
    }

    salesTableReplacement_ds.executeQuery();

    return ret;
}

display OrderType getOrderType(SalesTable _salesTable)
{
    return OrderType::Return;
}

