public void selectionChanged()
{
    boolean enableButton;
    super();

    btnReplacementInvoice.enabled(!salesTableReplacement.ProjId && salesTableReplacement.lastInvoice() != '');

    enableButton = RetailMCRChannelTable::mcrEnableOrderCompletion(salesTableReplacement)
                    && salesTableReplacement.type().mcrAllowPaymentAccess();
    btnReplacementPayments.enabled(enableButton);
}

