public void init()
{
    QueryBuildDataSource    qbds;

    super();

    // Cache appropriate display methods.
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayLastShipDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrOrderReleaseDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayDeliveryName));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayTender));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrHasExpeditedLines));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrOrderReleaseDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrGetPaymStatus));

    qbds = this.query().dataSourceTable(tableNum(SalesTable));
    qbds.clearDynalinks();
    qbds.addRange(fieldNum(SalesTable, SalesType)).value(queryValue(SalesType::Sales));

    mReturnItemNumRange = qbds.addRange(fieldNum(SalesTable, ReturnItemNum));
}

void clicked()
{
    CustInvoiceJour custInvoiceJourSelected;
    Args args = new args();

    select firstOnly custInvoiceJourSelected
        where custInvoiceJourSelected.SalesId == salesTable.SalesId;
    args.parm(salesTable.SalesId);
    args.parmEnum(enum2int(SalesPurchCopy::CreditNoteHeader));
    args.parmEnumType( EnumNum(SalesPurchCopy));

    args.record(custTable);
    args.lookupRecord(custInvoiceJourSelected);
    args.name(formstr(SalesCopying));
    new MenuFunction(MenuItemDisplayStr(MCRReturnFindSalesOrder),MenuItemType::Display).run(args);
}

/// <summary>
/// Switches the button to be a regular button instead of a menu item.
/// </summary>
void clicked()
{
    Args            args;
    formrun         formrun;
    MCRCustTableConfigure custTableConfigure = new MCRCustTableConfigure();

    args = new Args();

    custTableConfigure.parmShowCustomerServiceButton(false);
    custTableConfigure.parmModifyCustAccount(custTable.AccountNum);

    args.parmObject(custTableConfigure);

    formrun = new MenuFunction(menuitemDisplayStr(CustTable), MenuItemType::Display).create(args);
    if (formrun)
    {
        formrun.run();
        formrun.wait();
        custTable_ds.reread();
        custTable_ds.refresh();

        // If the parmMap indicates a different customer then the customer currently selected
        // change to that new selected customer.
        if (args)
        {
            custTableConfigure = args.parmObject();
            if (custTableConfigure.parmCustAccount() != "" && custTableConfigure.parmCustAccount() != custTable.AccountNum && CustTable::exist(custTableConfigure.parmCustAccount()))
            {
                element.ChangeCust(custTableConfigure.parmCustAccount());
            }
        }
    }
}

