        buttonLineInventTransGoodsInRoute_RU.visible(salesLine.hasInventTransGoodsInRoute_RU());
    }

    super();
}

public str toolTip()
{
    DictEnum    dictEnum = new DictEnum(enumNum(LineDeliveryType));

    return dictEnum.label() + ': ' + dictEnum.value2Name(salesLine.LineDeliveryType);
}

public boolean modified()
{
    boolean ret;
    boolean recordsCreated;
    EcoResProductRecId productRecId;

    if (isConfigurationkeyEnabled(configurationKeyNum(TradeItemSearch)))
    {
        [productRecId, recordsCreated] = MCRInventSearch::searchProduct(salesLine, salesLine_ItemId.text());

        // DataSource ItemId.modified() is called on the MCRSalesQuickQuoteForm, if a product is returned then
        //  it means the line was not updated there, so set ItemId and update product dimensions based on returned product.
        if (productRecId)
        {
            InventDistinctProduct distinctProduct = InventDistinctProduct::findByProduct(productRecId);
            ItemId newItemId = distinctProduct ? distinctProduct.ItemId : InventTable::findByProduct(productRecId).ItemId;
            // If the user entered an item ID, skip refresh
            if (newItemId != salesLine_ItemId.text())
            {
                salesLine.ItemId = newItemId;
                salesLine_ds.refresh();
            }
            ret = super();

            if (distinctProduct)
            {
                element.updateProductDimensions(distinctProduct);
            }
        }
        else
        {
            ret = recordsCreated;
        }
    }
    else
    {
        ret = super();
    }

    if (mcrCallCenterOrder)
    {
        element.mcrSetLineControls();
    }

    return ret;
}

public void lookup()
{
    element.performCustInvoiceJourLookup_MY(
        this,
        queryValue(RefNum::SalesOrder) + "," + queryValue(RefNum::Project),
        salesTable.CustAccount);
}

void clicked()
{
    SalesTableType menuSalesTableType = salesTable.type();

    salesTableForm_return.enableLineInventoryButtons(salesLine, buttonLineGridInventTrans, buttonLineGridInventOnHand, buttonLineGridInventLot, null);

    buttonLineGridInventOnhandInterCompany.enabled(salesTableForm_return.allowEnableInterCompanyOnHandButton(salesLine));
}

/// <summary>
/// Clones the inquiry from the <c>SalesTable</c> form.
/// </summary>
void  clicked()
{
    // Only display the continuity data option when it is a continuity order.
    if (mcrSalesTableReturn.ContinuityOrder)
    {
        continuityData.enabled(true);
    }
    else
    {
        continuityData.enabled(false);
    }

    soCreateReturn.enabled(false);

    super();
}

void clicked()
{
    MCRSalesOrderCancellation::cancelOrder(salesTableReturn);
}

void clicked()
{
    Args args = new Args();
    FormRun         menuFunction;
    SalesTable      currentRecord;

    // Save currently selected record
    currentRecord = salesTableReturn.data();

    args.caller(element);
    args.record(salesTableReturn);

    menuFunction = new MenuFunction(menuitemDisplayStr(MCRHoldCodeTransSOManage), MenuItemType::Display).create(args);
    menuFunction.run();
    menuFunction.wait();
    salesTableReturn_ds.executeQuery();

    // Re-select the previously selected record
    salesTableReturn_ds.positionToRecord(currentRecord);
}

