{
    super();
    element.mcrReturnInquiry_Order();
}

void clicked()
{
    Args recapArgs;
    FormRun recapFormRun;
    MCRSalesOrderRecapConfigure salesOrderRecapConfigure = new MCRSalesOrderRecapConfigure();

    recapArgs = new Args();

    recapArgs.record(salesTableReturn);

    recapFormRun = classfactory.formRunClass(recapArgs);

    salesOrderRecapConfigure.parmShowTotalsOnly(true);
    salesOrderRecapConfigure.parmShowModifyOrderButton(true);
    recapArgs.parmObject(salesOrderRecapConfigure);
    recapFormRun = new MenuFunction(menuitemDisplayStr(MCRSalesOrderRecap), MenuItemType::Display).create(recapArgs);

    recapFormRun.run();
    recapFormRun.wait();
}

/// <summary>
/// Loads the tax form for the sales order.
/// </summary>
void clicked()
{
    SalesTotals salesTotals;
    FormRun     callerFormRun = null;

    salesTotals = SalesTotals::construct(salesTableReturn);
    salesTotals.calc();
    salesTotals.tax().sourceSingleLine(true, true);
    Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkSalesOrder), salesTotals.tax(), salesTableReturn);
}

/// <summary>
/// Clones the inquiry from the <c>SalesTable</c> form.
/// </summary>
void  clicked()
{
    // Only display the continuity data option when it is a continuity order.
    if (mcrSalesTableReplacement.ContinuityOrder)
    {
        copyOfContinuityData.enabled(true);
    }
    else
    {
        copyOfContinuityData.enabled(false);
    }

    if (element.isReturnOrderAllowed(salesTableReplacement.SalesId))
    {
        CopyOfSoCreateReturn.enabled(true);
    }
    else
    {
        CopyOfSoCreateReturn.enabled(false);
    }

    super();
}

