public void init()
{
    super();

    if (RetailPricingHideMCRPriceOverrideConfigFlight::instance().isEnabled())
    {
        this.hideMCRPriceOverrideCheckboxes();
    }
}

private void hideMCRPriceOverrideCheckboxes()
{
    SalesTableGroup_SalesTablePriceOverride.visible(#MCRPriceOverrideEnabled);
    SalesLineGroup_PriceOverride.visible(#MCRPriceOverrideEnabled);
    MCROrderEventSetup_SalesQuoteTablePriceOverride.visible(#MCRPriceOverrideEnabled);
    MCROrderEventSetup_SalesQuoteLinePriceOverride.visible(#MCRPriceOverrideEnabled);

    // Hide SalesQuotationTabPage if it's empty.
    int controlCount = SalesQuotationGroup.controlCount();
    boolean hasVisibleControl = false;
    for (int controlIndex = 1; controlIndex <= controlCount; controlIndex ++)
    {
        if (SalesQuotationGroup.controlNum(controlIndex).isVisible())
        {
            hasVisibleControl = true;
            break;
        }
    }
    if (!hasVisibleControl)
    {
        SalesQuotationTabPage.visible(false);
    }
}

/// <summary>
/// Clears the user relation on modified.
/// </summary>
/// <remarks>
/// If the selection is <c>TableGroupAll::All</c>, disable the user relation field.
/// </remarks>
public void modified()
{
    super();

    // Clear the selection if the user code has changed.
    MCROrderEventSetup.UserRelation = '';

    if (MCROrderEventSetup.UserCode == TableGroupAll::All)
    {
        MCROrderEventSetup_DS.object(fieldNum(MCROrderEventSetup, UserRelation)).allowEdit(false);
    }
    else
    {
        MCROrderEventSetup_DS.object(fieldNum(MCROrderEventSetup, UserRelation)).allowEdit(true);
    }
}
