Method.Source:
[Form]
public class MCROrderEventTable extends FormRun implements SysIFilterConsumerForm
{
}

/// <summary>
///     Hides all detail groups.
/// </summary>
void hideAllDetailGroups()
{
    // hide the hold code detail information
    OrderHoldGeneralGroup.visible(false);
    DropShipGroup.visible(false);
    PriceAndMiscChargeGroup.visible(false);
    OnlyCommentGroup.visible(false);
    AlternateGroup.visible(false);
    ReturnsGroup.visible(false);
    DeliveryDateGroup.visible(false);
    SalesOrderDeliveryAddressGroup.visible(false);
    EventDescriptionGroup.visible(false);
    OrderCreditsGroup.visible(false);
    SalesLineOriginalAndOverrideDateGroup.visible(false);
}

/// <summary>
///     Hides the overview tab
///     and resize the form.
/// </summary>
public void init()
{
    super();

    if (element.args().caller() && element.args().caller().name() == formStr(MCRCustomerService))
    {
        Grid.visible(false);
    }
}

void mcrReturnInquiry()
{
    SalesTable returnSalesTable = SalesTable::findRecId(MCROrderEventTable.RefRecId);

    MCRReturnTableConfigure returnTableConfigure = new mcrReturnTableConfigure();
    returnTableConfigure.parmSalesId(returnSalesTable.SalesId);

    Args formArgs = new Args();
    formArgs.parmObject(returnTableConfigure);
    formArgs.caller(this);

    FormRun formRun = new MenuFunction(menuitemDisplayStr(ReturnTableDetails), MenuItemType::Display).create(formArgs);
    formRun.run();
    formRun.wait();
}

/// <summary>
/// Event raised when showing detail groups.
/// </summary>
/// <param name="_eventType">The type of event.</param>
/// <param name="_formRun">The form raising the event.</param>
delegate void showingDetailGroups(MCROrderEventType _eventType, FormRun _formRun)
{
}

/// <summary>
///     Shows the adequate groups depending on the event type.
/// </summary>
void showDetailGroups()
{
    // Hide all groups
    element.hideAllDetailGroups();

    SalesOrderInquiry.enabled(MCROrderEventTable.QuotationId ? false: true);

    switch (MCROrderEventTable.MCROrderEventType)
    {
        //=====================================================
        // Hold codes
        //=====================================================
        case MCROrderEventType::AddSOOrderHold:
        case MCROrderEventType::ModifySOOrderHold:
        case MCROrderEventType::AddSOFraudHold:
            OrderHoldGeneralGroup.visible(true);
            OnlyCommentGroup.visible(true);
            break;
        case MCROrderEventType::RemoveSOOrderHold:
        case MCROrderEventType::RemoveSOFraudHold:
            OrderHoldGeneralGroup.visible(true);
            OnlyCommentGroup.visible(true);
            break;
        //=====================================================
        // Sales order
        //=====================================================

        case MCROrderEventType::CreateSalesOrder:
            DeliveryDateGroup.visible(true);
            break;
        case MCROrderEventType::ModifySLDeliveryAddress:
            SalesOrderDeliveryAddressGroup.visible(true);
            DeliveryDateGroup.visible(true);
            break;
        case MCROrderEventType::ModifySLRequestedReceiptDate:
        case MCROrderEventType::ModifySLRequestedShipDate:
        case MCROrderEventType::ModifySLConfirmedReceiptDate:
        case MCROrderEventType::ModifySLConfirmedShipDate:
            SalesLineOriginalAndOverrideDateGroup.visible(true);
            break;
        case MCROrderEventType::ModifySalesOrder:
            // All fields shown for this group are always shown
            //  left case here so it would skip the rest
            break;

        //=====================================================
        // Drop shipments
        //=====================================================
        case MCROrderEventType::CancelDropShipPO:
        case MCROrderEventType::CreateDropShipPO:
        case MCROrderEventType::DeliverDropShipPO:
        case MCROrderEventType::ReleaseDropShipPO:
            DropShipGroup.visible(true);
            break;
        //********************************************************
        // PriceOverride and MiscCharges and PriceOverride
        //********************************************************
        case MCROrderEventType::SalesLinePriceOverrideTurnedOff:
        case MCROrderEventType::SalesLinePriceOverrideTurnedOn:
        case MCROrderEventType::SalesQuoteLinePriceOverrideTurnedOff:
        case MCROrderEventType::SalesQuoteLinePriceOverrideTurnedOn:
        case MCROrderEventType::MiscChargeOverrideTurnedOff:
        case MCROrderEventType::MiscChargeOverrideTurnedOn:
        case MCROrderEventType::MiscChargeAdd:
        case MCROrderEventType::ContinuityCustLinePriceOverrideTurnedOff:
        case MCROrderEventType::ContinuityCustLinePriceOverrideTurnedOn:
        case MCROrderEventType::SalesTablePriceOverrideTurnedOff:
        case MCROrderEventType::SalesTablePriceOverrideTurnedOn:
        case MCROrderEventType::SalesQuoteTablePriceOverrideTurnedOff:
        case MCROrderEventType::SalesQuoteTablePriceOverrideTurnedOn:
            PriceAndMiscChargeGroup.visible(true);
            break;
        //=====================================================
        //  Gift certificate events
        //=====================================================
        case MCROrderEventType::VoidGiftCertificate:
        case MCROrderEventType::ReprintGiftCertificate:
            DeliveryDateGroup.visible(true);
            break;
        //=====================================================
        // Hide details tab page if event is one of
        // the FTC event is FTCClockStarted, FTCClockStopped
        //=====================================================
        case MCROrderEventType::FTCClockStarted:
        case MCROrderEventType::FTCClockStopped:
        case MCROrderEventType::FTCFirstNotification:
        case MCROrderEventType::FTCSecondNotification:
        case MCROrderEventType::FTCBackOrderCancellation:
        case MCROrderEventType::FTCExemptOff:
        case MCROrderEventType::FTCExemptOn:
            // All fields shown for this group are always shown
            // left case here so it would skip the rest
            break;
        //=====================================================
        // Expedite group
        //=====================================================
        case MCROrderEventType::ExpediteLineOff:
        case MCROrderEventType::ExpediteLineOn:
        case MCROrderEventType::ExpediteOrderOff:
        case MCROrderEventType::ExpediteOrderOn:
            DeliveryDateGroup.visible(true);
            break;
        //=====================================================
        // Item substitution
        //=====================================================
        case MCROrderEventType::AlternateItemUsed:
        case MCROrderEventType::AltItemUsedPicking:
        case MCROrderEventType::AltItemUsedWorkbench:
            AlternateGroup.visible(true);
            break;

        //=====================================================
        // Return order events
        //=====================================================
        case MCROrderEventType::ModifyReturnOrder:
        case MCROrderEventType::ReturnCreated:
            ReturnsGroup.visible(true);
            break;
        case MCROrderEventType::OverPayment:
        case MCROrderEventType::OverpaymentRelease:
        case MCROrderEventType::UnderPayment:
        case MCROrderEventType::UnderpaymentRelease:
        case MCROrderEventType::OverCreditLimit:
        case MCROrderEventType::OverCreditLimitRelease:
        case MCROrderEventType::CheckHold:
        case MCROrderEventType::CheckHoldRelease:
            EventDescriptionGroup.visible(true);
            break;
        // Show event description for the 3 types
        // but only show reason code for creation
        case MCROrderEventType::OrderCreditCreated:
        case MCROrderEventType::PriceMatch:
            OrderCreditsGroup.visible(true);
        case MCROrderEventType::OrderCreditApplied:
            EventDescriptionGroup.visible(true);
            break;
        default:
            this.showingDetailGroups(MCROrderEventTable.MCROrderEventType, this);
            break;
    }

}

/// <summary>
///     Shows sales ID controls or quotation ID controls.
/// </summary>
/// <param name="_sales">
/// Indicates if the sales ID and quotation ID controls should be shown.
/// </param>
void showSalesOrQuotation(boolean _sales)
{
    MCROrderEventTable_ds.object(fieldNum(MCROrderEventTable, SalesId)).visible(_sales);
    MCROrderEventTable_ds.object(fieldNum(MCROrderEventTable, QuotationId)).visible(! _sales);
}

public int active()
{
    int ret;

    ret = super();
    element.showDetailGroups();
    return ret;
}

/// <summary>
///     Shows all order events when showing order level otherwise shows the line events.
/// </summary>
public void executeQuery()
{
    // query by the invent trans
    if (element.args().record())
    {
        QueryBuildDataSource mcrOrderEventTableDataSource = this.query().dataSourceTable(tableNum(MCROrderEventTable));

        switch (element.args().record().tableId)
        {
            case tableNum (SalesTable):
                element.showSalesOrQuotation(true);
                break;
            case tableNum(SalesLine):
                element.showSalesOrQuotation(true);
                SalesOrderInquiry.visible(false);
                SalesLine salesLine = element.args().record();
                mcrOrderEventTableDataSource.addRange(fieldNum(MCROrderEventTable, SalesId)).value(queryValue(salesLine.SalesId));
                mcrOrderEventTableDataSource.addRange(fieldnum(MCROrderEventTable, InventTransId)).value(queryValue(salesLine.InventTransId));
                break;
            // If coming from quotation, hide sales id
            case tableNum(SalesQuotationTable):
                element.showSalesOrQuotation(false);
                SalesOrderInquiry.visible(false);
                break;
            // Add case of coming from SalesQuotationLine
            case tableNum(SalesQuotationLine):
                element.showSalesOrQuotation(false);
                SalesOrderInquiry.visible(false);
                SalesQuotationLine salesQuotationLine = element.args().record();
                mcrOrderEventTableDataSource.addRange(fieldNum(MCROrderEventTable, QuotationId)).value(queryValue(salesQuotationLine.QuotationId));
                mcrOrderEventTableDataSource.addRange(fieldnum(MCROrderEventTable, InventTransId)).value(queryValue(salesQuotationLine.InventTransId));
                break;
            case tableNum(MCROrderEventTable):
                mcrOrderEventTableDataSource.clearRange(fieldNum(MCROrderEventTable, RecId));
                MCROrderEventTable orderEventTable = element.args().record();
                mcrOrderEventTableDataSource.addRange(fieldNum(MCROrderEventTable, RecId)).value(queryValue(orderEventTable.RecId));
                break;

            default:
                break;
        }
    }
    super();

    //disable the button if no records selected
    MCRReturnInquiry.enabled(MCROrderEventTable.RecId);
}

/// <summary>
///     Loads the return inquiry form.
/// </summary>
void clicked()
{
    super();
    element.mcrReturnInquiry();
}
