/// <summary>
///     Loads a temporary table of sequence numbers that should show on the
///     sequence number grid.
/// </summary>
public void init()
{
    this.numberSeqPreInit();

    super();

    this.numberSeqPostInit();

    if (FeatureStateProvider::isFeatureEnabled(RetailRestrictCreditCardUsageFeature::instance()))
    {
        MCROrderParameters_MCRAllowCustomerCardOnFile.visible(true);
    }
    else
    {
        MCROrderParameters_MCRAllowCustomerCardOnFile.visible(false);
    }
}

/// <summary>
///     Loads the number sequence grid with Order management sequence numbers that need to be defined.
/// </summary>
void numberSeqPostInit()
{
    numberSequenceReference_ds.object(fieldnum(NumberSequenceReference, AllowSameAs)).visible(numberSeqApplicationModule.sameAsActive());
    referenceSameAsLabel.visible(numberSeqApplicationModule.sameAsActive());
}

/// <summary>
///     Loads the number sequence grid with order management sequence numbers that need to be defined.
/// </summary>
void numberSeqPreInit()
{
    runExecuteDirect   = false;
    numberSequenceModules = [NumberSeqModule::MCROrderManagement];
    numberSeqApplicationModule = new MCRNumberSeqModuleOrderManagement();

    // Define the right scope of the Number Sequence
    scope = NumberSeqScopeFactory::createDataAreaScope();

    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

/// <summary>
///     Resets the values on the future order related field if
///     the user has unchecked the future order processing or if it
///     is the first time the customer parameters form is being launched and
///     the future orders processing is not checked.
/// </summary>
/// <remarks>
/// It also disables all the
///     future order related fields if the future order processing is not
///     turned on.
///     The release reserve flag is conditionally disabled based on the
///     <c>MCRFutureOrderAuth</c> flag.
/// </remarks>
void mcrFutureOrderDisabled()
{
    MCROrderParameters.MCRFutureOrderAuth = NoYes::No;
    MCROrderParameters.MCRFutureOrderDays = 0;
    MCROrderParameters.MCRFutureReleaseReserved = NoYes::No;

    MCROrderParameters_MCRFutureOrderAuth.enabled(false);
    MCROrderParameters_MCRFutureOrderDays.enabled(false);
    if (MCROrderParameters.MCRFutureOrderAuth == Noyes::No)
    {
        MCROrderParameters_MCRFutureReleaseReserved.enabled(false);
    }
    else
    {
        MCROrderParameters_MCRFutureReleaseReserved.enabled(true);
    }
}

/// <summary>
///     Enables the future order related fields.
/// </summary>
/// <remarks>
/// The release reserve flag is conditionally disabled based on the
///     <c>MCRFutureOrderAuth</c> flag.
/// </remarks>
void mcrFutureOrderEnabled()
{
    MCROrderParameters_MCRFutureOrder.enabled(true);
    MCROrderParameters_MCRFutureOrderAuth.enabled(true);
    MCROrderParameters_MCRFutureOrderDays.enabled(true);
    if (MCROrderParameters.MCRFutureOrderAuth == NoYes::Yes)
    {
        MCROrderParameters_MCRFutureReleaseReserved.enabled(true);
    }
    else
    {
        MCROrderParameters_MCRFutureReleaseReserved.enabled(false);
    }
}

        /// <summary>
        ///     Enables the future release reserved check box if the future order
        ///     authorizations is checked; disable otherwise.
        /// </summary>
        /// <returns>
        /// The identifier of the active record on the grid.
        /// </returns>
        public int active()
        {
            int ret;

            ret = super();

            // Future order processing
            if (MCROrderParameters.MCRFutureOrder == NoYes::Yes)
            {
                this.mcrFutureOrderEnabled();
            }
            else
            {
                this.mcrFutureOrderDisabled();
            }

			MCROrderParameters_SecurityOverrideCode.visible(mcrOrderParameters.MCRAllowReturnPaymentOverride == NoYes::Yes);

            MCROrderParameters_MCRPriceOverrideLineColor.backgroundColor(MCROrderParameters.MCRPriceOverrideLineColor);
            MCROrderParameters_MCRReturnOrderColor.backgroundColor(MCROrderParameters.MCRReturnOrderColor);
            MCROrderParameters_MCRSOWithReturnOrderColor.backgroundColor(MCROrderParameters.MCRSOWithReturnOrderColor);
            MCROrderParameters_MCRReplacementOrderColor.backgroundColor(MCROrderParameters.MCRReplacementOrderColor);

            return ret;
        }

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    // don't require a default source code for exchanges if the system is setup to allow expired
    // source codes.
    if (MCROrderParameters.MCRDefaultExchangeSourceID == ""
        && MCROrderParameters.MCRSourceCodeRequired == NoYes::Yes
        && MCROrderParameters.MCRAllowExpiredSourceCode == NoYes::No)

    {
        ret = checkFailed("@MCR23362");
    }

    return ret;
}

