    super();

    MCROrderParameters_ds.write();
}

/// <summary>
///     Loads the seqeunce number grid based on the temporary table load that was done on the init method of the form.
/// </summary>
void executeQuery()
{
    if (runExecuteDirect)
    {
        super();
    }
    else
    {
        runExecuteDirect = true;

        this.queryRun(NumberSeqReference::buildQueryRunMulti(numberSequenceReference,
                                                tmpIdRef,
                                                numberSequenceTable,
                                                numberSequenceModules,
                                                scope));

        numbersequenceReference_ds.research();
    }
}

/// <summary>
///     Loads the seqeunce number grid based on the temporary table load that was done on the init method of the form.
/// </summary>
void removeFilter()
{
    runExecuteDirect = false;
    numbersequenceReference_ds.executeQuery();
}

/// <summary>
///     Makes the other fields
///     that are related to future orders visible when the future order flag is checked; otherwise makes the rest of the
///     fields invisible.
/// </summary>
/// <returns>
/// true when the field has been modified; otherwise, false.
/// </returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    if (MCROrderParameters.MCRFutureOrder == NoYes::yes)
    {
        MCROrderParameters_ds.mcrFutureOrderEnabled();
    }
    else
    {
        MCROrderParameters_ds.mcrFutureOrderDisabled();
    }

    return ret;
}

/// <summary>
///     Enables the future release reserved check box if the future order authorization flag is turned off, otherwise the flag is disabled.
/// </summary>
/// <returns>
/// true when the field has been modified; otherwise, false.
/// </returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    if (this.value()==NoYes::yes)
    {
        MCROrderParameters_MCRFutureReleaseReserved.enabled(true);
    }
    else
    {
        MCROrderParameters.MCRFutureReleaseReserved = NoYes::No;
        MCROrderParameters_ds.refresh();
        MCROrderParameters_MCRFutureReleaseReserved.enabled(false);
    }
    return ret;
}

public void lookup()
{
    sysTableLookup lookup =
    sysTableLookup::newParameters(tableNum(CustPaymModeTable), this);
    lookup.addLookupfield(fieldnum(CustPaymModeTable, PaymMode));
    lookup.addLookupfield(fieldnum(CustPaymModeTable, name));
    lookup.performFormLookup();
}

/// <summary>
/// When AllowPaymentOverride is checked and unchecked makes
/// security override code visible and invisible respectively.
/// </summary>
/// <returns>Boolean value when checkbox is modified.</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (this.checked())
    {
        MCROrderParameters_SecurityOverrideCode.visible(true);
        warning("@Ret_HF_3920417:PaymentOverrideWarningMessage");
    }
    else
    {
        MCROrderParameters_SecurityOverrideCode.Text('');
        MCROrderParameters_SecurityOverrideCode.visible(false);
    }

    return ret;
}

