[Form]
    /// <summary>
    ///     The <c>MCROverCreditLimitWB</c> form makes it so all orders that put
    ///     a customer over their credit limit are viewable
    ///     from one location.
    /// </summary>
    /// <remarks>
    /// This form also allows a user to cancel/modify the order,
    ///     change the customers credit limit, or allow the
    ///     order to be processed.
    /// </remarks>
    public class MCROverCreditLimitWB extends FormRun
    {
        FormObservableLink statsChanged;
        real custCreditMax;
        CustCreditRating custCreditRating;
        MCRCustPaymTable currentRecord;

}

/// <summary>
    ///     Runs the cancellation logic.
    /// </summary>
    void mcrPromptCancelOrder()
    {
        MCRSalesOrderCancellation::cancelOrder(salesTable);

        // refresh after updates
        salesTable_ds.executeQuery();
        mcrCustPaymTableSO_ds.executeQuery();

        element.setOverallDisplays();
    }

/// <summary>
    ///     Sets all the values that are displayed on the top of the form.
    ///</summary>
    void setOverallDisplays()
    {
        statsChanged.markChanged();
    }

/// <summary>
    /// Initializes <c>FormObservableLink</c> variables.
    /// </summary>
    public void init()
    {
        super();

        statsChanged = new FormObservableLink();
    }

/// <summary>
///     Returns the number of orders in the payment table
/// </summary>
/// <returns>
/// The number of orders in the payment table
///     that have a over credit limit payment status.
/// </returns>
display Integer displayOverallTotalOrders()
{
    statsChanged.observe();
    return MCRCustPaymTable::getOverallTotalOrders(MCRCustPaymStatus::Declined, MCRCustPaymType::OnAccount);
}

/// <summary>
///     Returns the date of the oldest order that has
/// </summary>
/// <returns>
/// The date of the oldest order that has
///     an over credit limit payment status.
/// </returns>
display TransDate displayOverallOldestOverCreditDate()
{
    statsChanged.observe();
    return MCRCustPaymTable::getOverallOldestDate(MCRCustPaymStatus::Declined, MCRCustPaymType::OnAccount);
}

/// <summary>
        ///     Filters out orders that do not have a status
        ///     of over credit limit.
        /// </summary>
        public void init()
        {
            super();

            this.query().dataSourceTable(tableNum(MCRCustPaymTable))
                        .addRange(fieldNum(MCRCustPaymTable, Status))
                        .value(enum2str(MCRCustPaymStatus::Declined));

            this.query().dataSourceTable(tableNum(MCRCustPaymTable))
                        .addRange(fieldNum(MCRCustPaymTable, CustPaymType))
                        .value(enum2str(MCRCustPaymType::OnAccount));

            this.cacheAddMethod(identifierStr(displayDaysOnHold));
            this.cacheAddMethod(tableMethodStr(MCRCustPaymTable,getCurrency));
        }

/// <summary>
        ///     Displays the number of days a payment
        /// </summary>
        /// <param name="_mcrCustPaymTable">
        /// The payment record to evaluate.
        /// </param>
        /// <returns>
        /// The number of days a payment
        ///     has been on check hold.
        /// </returns>
        display Integer displayDaysOnHold(MCRCustPaymTable _mcrCustPaymTable)
        {
            return (MCRCustPaymTable::getDaysSinceCreation(_mcrCustPaymTable.RecId));
        }

/// <summary>
        ///     Returns the credit limit of the customer.
        /// </summary>
        /// <returns>
        /// The credit limit of the customer.
        /// </returns>
        display String15 displayCustCreditLimit()
        {
            CreditMaxMST custCredit = 0;

            custCredit = CustTable::find(mcrCustPaymTableSO.CustAccount).CreditMax;

            if (custCredit > 0)
            {
                return (num2str(custCredit, 4, 2, 1, 2));
            }
            else
            {
                return ("@MCR11201");
            }
        }

display CreditRating displayCustCreditRating()
        {
            CreditRating retVal;

            retVal = CustTable::find(mcrCustPaymTableSO.CustAccount).CreditRating;

            if (retVal)
            {
                return (retVal);
            }
            else
            {
                return ("@MCR25402");
            }
        }

/// <summary>
        ///     Gets the customer account name associated with the sales order or the invoice.
        /// </summary>
        /// <param name="_mcrCustPaymTable">
        /// The customer payment record used to find the sales order or invoice.
        /// </param>
        /// <returns>
        /// The customer account name associated with the sales order or the invoice.
        /// </returns>
        public display CustName displayCustName(MCRCustPaymTable _mcrCustPaymTable)
        {
            if (_mcrCustPaymTable.RefTableId == tableNum(SalesTable))
            {
                return CustTable::find(_mcrCustPaymTable.CustAccount).name();
            }
            else
            {
                return CustInvoiceTable::findRecId(_mcrCustPaymTable.RefRecId).Name;
            }
        }

public void init()
        {
            super();

            this.query().dataSourceTable(tableNum(MCRCustPaymTable))
                        .addRange(fieldNum(MCRCustPaymTable, Status))
                        .value(enum2str(MCRCustPaymStatus::Declined));

            this.query().dataSourceTable(tableNum(MCRCustPaymTable))
                        .addRange(fieldNum(MCRCustPaymTable, CustPaymType))
                        .value(enum2str(MCRCustPaymType::OnAccount));

            this.cacheAddMethod(tableMethodStr(MCRCustPaymTable,getCurrency));
        }

/// <summary>
        ///     Displays the number of days a payment
        ///     has been on check hold.
        /// </summary>
        /// <param name="_mcrCustPaymTable">
        /// The payment to evaluate.
        /// </param>
        /// <returns>
        /// The number of days a payment
        ///     has been on check hold.
        /// </returns>
        display Integer displayDaysOnHold(MCRCustPaymTable _mcrCustPaymTable)
        {
            return (MCRCustPaymTable::getDaysSinceCreation(_mcrCustPaymTable.RecId));
        }

/// <summary>
        ///  Cancels the order.
        /// </summary>
        void clicked()
        {
            if (salesTable)
            {
                element.mcrPromptCancelOrder();
            }
            else
            {
                info("@MCR25046");
            }
        }

/// <summary>
        ///     Authorizes the currently selected sales order on account payment.
        /// </summary>
        void clicked()
        {
            MCROrderEventTable mcrOrderEventTable;
            CustTable         custTable;

            CustCreditLimit   custCreditLimit;
            boolean           ret = true;
            AmountCur         totalCreditBalance = 0;

        if (mcrCustPaymTableSO)
            {
                custTable = CustTable::find(mcrCustPaymTableSO.CustAccount, true);

                // Reworked credit limit checking to use centralized function.
                custCreditLimit = CustCreditLimit::construct(custTable);
                custCreditLimit.mcrDisplayMessage(false);
                ret = custCreditLimit.check();
                totalCreditBalance = custCreditLimit.mcrBalanceCredit();

                mcrCustPaymTableSO.Status = MCRCustPaymStatus::Posted;
                mcrCustPaymTableSO.OnAccountAuthorized = NoYes::Yes;

                mcrCustPaymTableSO.update();
                MCREndOrder::endOrder(salesTable.SalesId, false);

                //  For some reason payment table update had to be after
                //      sales tables update.  So if end order isn't called
                //      still need to update the payment table
                mcrCustPaymTableSO.update();

                // Write an event saying who did the release
                if (totalCreditBalance > custTable.CreditMax)
                {
                    // The payment was released despite the fact that it put the customer over their credit limit
                    mcrOrderEventTable.insertPaymentEvent(MCROrderEventType::OverCreditLimitRelease, mcrCustPaymTableSO,
                            strFmt("@MCR24977", custTable.CreditMax,
                            (totalCreditBalance - custTable.CreditMax)));
                }
                else
                {
                    // The payment was released after the credit limit was changed
                    mcrOrderEventTable.insertPaymentEvent(MCROrderEventType::OverCreditLimitRelease, mcrCustPaymTableSO,
                            "@MCR24976");
                }

                mcrCustPaymTableSO_ds.executeQuery();
                salesTable_ds.executeQuery();

                element.setOverallDisplays();
            }
            else
            {
                info("@MCR25046");
            }

            super();
        }

/// <summary>
    ///     Authorizes the currently selected invoice on account payment.
    /// </summary>
    void clicked()
    {
        if (mcrCustPaymTableFT)
        {
            mcrCustPaymTableFT.Status = MCRCustPaymStatus::NotSubmitted;
            mcrCustPaymTableFT.OnAccountAuthorized = NoYes::Yes;
            mcrCustPaymTableFT.update();
            mcrCustPaymTableFT_ds.executeQuery();
            custInvoiceTable_ds.executeQuery();

            element.setOverallDisplays();
        }
        else
        {
            info("@MCR25046");
        }

        super();
    }
