Method.Source:
[Form]
/// <summary>
///     The <c>MCRPickingWorkbench</c> form is used to create picking sessions and
///     then generate waves for those sessions.
/// </summary>
/// <remarks>
///     Posting of pickwaves calls the same functionality as the batch job for creating picklists,
///     except that the query used is created from the session profile.
///     Note: Before posting the picklist for sales orders, form reserves
///     inventory for the sales order and then sets the <c>SalesLine</c>
///     <c>SalesDeliverNow</c> and <c>InventDeliverNow</c> fields.
/// </remarks>
public class MCRPickingWorkbench extends FormRun
{
    QueryBuildRange sessionTypeRange;
    QueryBuildRange waveTypeRange;

    // Flag that is set to drive if warnings will be displayed when
    // item was selected but cannot be reserved.
    boolean    displayWarning;
}

/// <summary>
///     Clears the criteria when the user switches the type of search to be used
///     to load the picking groups.
/// </summary>
void clearAllCriteria()
{
    queryValue.text("");
    MCRPickingWorkbenchSession.InitProfileId    = "";
    MCRPickingWorkbenchSession.DlvMode          = "";
    MCRPickingWorkbenchSession.MCRExpedite      = "";
    MCRPickingWorkbenchSession.DlvDate          = datenull();
    MCRPickingWorkbenchSession.Warehouse        = "";
    MCRPickingWorkbenchSession.TenderTypeId     = "";
    MCRPickingWorkbenchSession.MaxLinesPerOrder = 0;
    MCRPickingWorkbenchSession.MaxOrders        = 0;
}

/// <summary>
///     Enables and disables the search criteria groups
///     depending if a prompt option was selected (since
///     prompts override other search criteria).
/// </summary>
void enableCriteriaGroups()
{
    boolean enabled;

    enabled = ! (MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::ByItem
                || MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::BySalesID
                || MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::ByContinuity);

    OrderInformation.visible(enabled);
    LineInformation.visible(enabled);
    Search.visible(!enabled);
}

public str formatString()
{
    str formattedString;

    formattedString = queryValue.text();

    // The following numebers are ascii numbers 10 = new line feed, // 13 = carriage return, 32 = space
    formattedString = strreplace(formattedString, num2char(13), num2char(32));
    formattedString = strreplace(formattedString, num2char(10), num2char(32));

    if (formattedString == "")
    {
        formattedString = "..";
    }

    return formattedString;
}

/// <summary>
///     Enables and disables the search criteria groups
///     depending if a prompt option was selected (since
///     prompts override other search criteria).
/// </summary>
public void init()
{
    super();

    element.enableCriteriaGroups();
    Prompts.allowEdit(true);

    if (!MCRPickingWorkbenchWave)
    {
        PrintGroup.enabled(false);
    }
}

public void run()
{
    super();

    // initialize the combo box
    WaveType.add("@SYS14437");      // All
    WaveType.add("@SYS11408");  // Created
    WaveType.add("@SYS5308");  // Printed
    WaveType.add("@SYS54623"); // Completed
    WaveType.selection(0);

    // initialize the combo box
    SessionType.add("@SYS14437");      // All
    SessionType.add("@SYS16011");     // Open
    SessionType.add("@SYS11408");  // Created
    SessionType.add("@SYS5308");  // Printed
    SessionType.add("@SYS54623"); // Completed
    SessionType.selection(0);

    // Display reservation warnings in the info log.
    //  Note: hardcoded value for now.
    displayWarning = true;
}

public edit NoYes editComplete(boolean                  _set,
                                MCRPickingWorkbenchWave _mcrPickingWorkbenchWave,
                                NoYes                   _checkboxValue)
{
    if (_set)
    {
        // safety error checking
        if (MCRPickingWorkbenchWave.WaveStatus != MCRPickingWaveStatus::Printed)
        {
            throw error("@MCR11546");
        }
        MCRPickingWorkbenchWave::completeWave(MCRPickingWorkbenchWave);

        // Refresh grids
        MCRPickingWorkbenchWave_ds.executeQuery();
        MCRPickingWorkbenchSession_ds.research(true);
    }

    if (_mcrPickingWorkbenchWave.WaveStatus == MCRPickingWaveStatus::Completed)
    {
        _checkboxValue = NoYes::Yes;
        this.allowEdit(false);
    }
    else
    {
        _checkboxValue = NoYes::No;
        this.allowEdit(true);
    }
    return _checkboxValue;
}

/// <summary>
///     Initializes the <c>MCRPickingWorkbenchWave</c> datasource.
/// </summary>
public void init()
{
    QueryBuildDataSource    queryBuildDataSource;
    super();

    this.cacheAddMethod(tableMethodStr(MCRPickingWorkbenchWave, displayNumItems));
    this.cacheAddMethod(tableMethodStr(MCRPickingWorkbenchWave, displayNumOrders));

    // initialze range for query
    queryBuildDataSource = this.query().dataSourceTable(tableNum(MCRPickingWorkbenchWave));
    WaveTypeRange = queryBuildDataSource.addRange(fieldNum(MCRPickingWorkbenchWave, WaveStatus));
}

public int active()
{
    int ret;

    ret = super();

    if (MCRPickingWorkbenchWave)
    {
        PrintGroup.enabled(true);
    }
    else
    {
        PrintGroup.enabled(false);
    }

    WMSPickingRoute_ds.executeQuery();

    return ret;
}

public edit NoYes editComplete(boolean                      _set,
                                MCRPickingWorkbenchSession  _mcrPickingWorkbenchSession,
                                NoYes                       _checkboxValue)
{
    MCRPickingWorkbenchWave mcrPickingWorkbenchWaveLocal;
    boolean allWavesCompleted = true;

    if (_set)
    {
        ttsBegin;
        while select forUpdate mcrPickingWorkbenchWaveLocal
            where mcrPickingWorkbenchWaveLocal.SessionId == MCRPickingWorkbenchSession.SessionId
        {
            if (mcrPickingWorkbenchWaveLocal.WaveStatus == MCRPickingWaveStatus::Printed)
            {
                MCRPickingWorkbenchWave::completeWave(mcrPickingWorkbenchWaveLocal);
            }
            else if (mcrPickingWorkbenchWaveLocal.WaveStatus == MCRPickingWaveStatus::Created)
            {
                throw error("@MCR11546");
            }
        }
        ttsCommit;
        // Refresh grids
        MCRPickingWorkbenchWave_ds.executeQuery();
        MCRPickingWorkbenchSession_ds.research(true);
    }

    if (_mcrPickingWorkbenchSession.SessionStatus == MCRPickingSessionStatus::Completed)
    {
        _checkboxValue = NoYes::Yes;
    }
    else
    {
        _checkboxValue = NoYes::No;
    }

    return _checkboxValue;
}

public void create(boolean _append = false)
{
    this.first();
    super(_append);

    element.clearAllCriteria();
    MCRPickingWorkbenchSession.InitProfileId = "";

    MCRPickingWorkbenchSession.Prompt = MCRPickingPrompt::ByCriteria;
    element.enableCriteriaGroups();
    this.write();
}

/// <summary>
///     Creates a combobox to be used as a range for what status of sessions
///     should be shown.
/// </summary>
public void init()
{
    QueryBuildDataSource    queryBuildDataSource;
    super();

    // initialze range for query
    queryBuildDataSource = this.query().dataSourceTable(tableNum(MCRPickingWorkbenchSession));
    sessionTypeRange = queryBuildDataSource.addRange(fieldNum(MCRPickingWorkbenchSession, SessionStatus));
    queryBuildDataSource.addSortField(fieldNum(MCRPickingWorkbenchSession, CreatedDateTime), SortOrder::Descending);
}

public int active()
{
    int ret;

    ret = super();

    SessionComplete.allowEdit(MCRPickingWorkbenchSession.SessionStatus != MCRPickingSessionStatus::Completed);

    // only enable the 'generate waves' button for sessions that are 'open'
    if (MCRPickingWorkbenchSession.SessionStatus == MCRPickingSessionStatus::Open)
    {
        GenerateGroups.enabled(true);
        Prompts.allowEdit(true);
        PrintSessionGroup.enabled(false);
        ProfileInit.enabled(true);
        Search.enabled(true);
        OrderInformation.enabled(true);
        LineInformation.enabled(true);
        Maximums.enabled(true);
    }
    else
    {
        GenerateGroups.enabled(false);
        Prompts.allowEdit(false);
        PrintSessionGroup.enabled(true);
        ProfileInit.enabled(false);
        Search.enabled(false);
        OrderInformation.enabled(false);
        LineInformation.enabled(false);
        Maximums.enabled(false);
    }

    element.enableCriteriaGroups();

    if (MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::ByItem)
    {
        queryValue.label("@MCR11409");
    }
    else if (MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::ByContinuity)
    {
        queryValue.label("@MCR12876");
    }
    else if (MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::BySalesID)
    {
        queryValue.label("@MCR11410");
    }

    WMSPickingRoute_ds.executeQuery();

    return ret;
}

public void modified()
{
    super();
    MCRPickingWorkbenchSession_ds.active();
}

public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;

    queryBuildDataSource = this.query().dataSourceName(WMSOrderTrans_ds.name());
    queryBuildDataSource.clearRange(fieldNum(WMSOrderTrans, routeID));

    if (!MCRPickingWorkbenchWave.WaveReferenceId || !wmspickingroute.pickingRouteID)
    {
        queryBuildDataSource.addRange(fieldNum(WMSOrderTrans, routeID)).value(SysQuery::valueNotEmptyString());
    }

    super();
}

public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;

    queryBuildDataSource = this.query().dataSourceName(WMSPickingRoute_ds.name());
    queryBuildDataSource.clearRange(fieldNum(WMSPickingRoute, MCRPickingWaveRef));

    if (MCRPickingWorkbenchWave.WaveReferenceId)
    {
        queryBuildDataSource.addRange(fieldNum(WMSPickingRoute, MCRPickingWaveRef)).value(sysQuery::value(MCRPickingWorkbenchWave.WaveReferenceId));
    }
    else
    {
        queryBuildDataSource.addRange(fieldNum(WMSPickingRoute, MCRPickingWaveRef)).value(SysQuery::valueNotEmptyString());
    }

    super();
}

void clicked()
{
    super();

    MCRPickingWorkbenchSession_ds.research(true);
}

void clicked()
{
    // Logic moved to a class so can be run in batch
    MCRPickingWorkbench::mcrGenerateWavesLogic(MCRPickingWorkbenchSession, element.formatString(), displayWarning);

    // Refresh grids
    MCRPickingWorkbenchWave_ds.executeQuery();
    MCRPickingWorkbenchSession_ds.research(true);
}

void clicked()
{
    MCRPickingWorkbenchWave mcrPickingWorkbenchWaveLocal;

    super();

    // print the waves
    while select mcrPickingWorkbenchWaveLocal
        where mcrPickingWorkbenchWaveLocal.SessionId == MCRPickingWorkbenchSession.SessionId
    {
        MCRPickingWorkbench::printPickWave(mcrPickingWorkbenchWaveLocal, false);
    }

    MCRPickingWorkbenchWave_ds.executeQuery();

    if (MCRPickingWorkbench::updateSessionPrintStatus(MCRPickingWorkbenchSession))
    {
        MCRPickingWorkbenchSession_ds.research(true);
    }
}

/// <summary>
///     Prints all waves for a session that are not of the status 'completed'.
/// </summary>
void clicked()
{
    MCRPickingWorkbenchWave mcrPickingWorkbenchWaveLocal;

    super();

    // print the waves
    while select mcrPickingWorkbenchWaveLocal
        where mcrPickingWorkbenchWaveLocal.SessionId == MCRPickingWorkbenchSession.SessionId
            && mcrPickingWorkbenchWaveLocal.WaveStatus != MCRPickingWaveStatus::Completed
    {
        MCRPickingWorkbench::printPickWave(mcrPickingWorkbenchWaveLocal);
    }

    MCRPickingWorkbenchWave_ds.executeQuery();

    if (MCRPickingWorkbench::updateSessionPrintStatus(MCRPickingWorkbenchSession))
    {
        MCRPickingWorkbenchSession_ds.research(true);
    }
}

/// <summary>
///     Prints only waves for a session that are of status 'created'.
/// </summary>
void clicked()
{
    MCRPickingWorkbenchWave mcrPickingWorkbenchWaveLocal;

    super();

    // print the waves
    while select mcrPickingWorkbenchWaveLocal
        where mcrPickingWorkbenchWaveLocal.SessionId == MCRPickingWorkbenchSession.SessionId
            && mcrPickingWorkbenchWaveLocal.WaveStatus == MCRPickingWaveStatus::Created
    {
        MCRPickingWorkbench::printPickWave(mcrPickingWorkbenchWaveLocal);
    }

    MCRPickingWorkbenchWave_ds.executeQuery();

    if (MCRPickingWorkbench::updateSessionPrintStatus(MCRPickingWorkbenchSession))
    {
        MCRPickingWorkbenchSession_ds.research(true);
    }
}

public int selectionChange()
{
    int ret;

    ret = super();

    // Update the range on the MCRPickingWorkbenchSession datasource
    // query depending on the value of the combobox.
    if (this.valueStr() == "@SYS14437") // all
    {
        sessionTypeRange.value("");
    }
    else if (this.valueStr() == "@SYS16011") // open
    {
        sessionTypeRange.value(enum2str(MCRPickingSessionStatus::Open));
    }
    else if (this.valueStr() == "@SYS11408") // created
    {
        sessionTypeRange.value(enum2str(MCRPickingSessionStatus::Created));
    }
    else if (this.valueStr() == "@SYS5308") // printed (includes printed/partiallyprinted)
    {
        sessionTypeRange.value(strfmt("%1, %2", enum2str(MCRPickingSessionStatus::PartiallyPrinted), enum2str(MCRPickingSessionStatus::Printed)));
    }
    else if (this.valueStr() == "@SYS54623") // completed
    {
        sessionTypeRange.value(enum2str(MCRPickingSessionStatus::Completed));
    }

    MCRPickingWorkbenchSession_ds.executeQuery();
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    // Uses an existing profile to set up the search criteria
    // for a picking workbench session.  On selecting a profile,
    // all fields are written over to match the profile.  Afterwards,
    // any fields can be modified to slightly differ from the profile used
    // to initialize the search criteria.
    MCRPickingWorkbenchSession.initFromPickingProfile(
                    MCRPickingWorkbenchSession.InitProfileId);
    MCRPickingWorkbenchSession_ds.refresh();

    element.enableCriteriaGroups();

    return ret;
}

public void lookup()
{
    InventTable::mcrlookupItemIds(this);
}

public void lookup()
{
    // Changes the string edit field lookup functionality to either
    // lookup item Ids or sales Ids depending on how it was called.
    if (MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::ByItem)
    {
        InventTable::mcrlookupItemIds(this);
    }
    else if (MCRPickingWorkbenchSession.Prompt == MCRPickingPrompt::ByContinuity)
    {
        InventTable::mcrlookupContinuityItems(this);
    }
    else // Sales orders  (in in-process status)
    {
        SalesTable::mcrLookupInWarehouseOrders(this);
    }
}

/// <summary>
///     Prints the picklists associated with a pickwave and update the status of the session.
/// </summary>
void clicked()
{
    MCRPickingWorkbenchWave mcrPickingWorkbenchWaveLocal;

    super();

    // print the wave(s)
    for (mcrPickingWorkbenchWaveLocal = MCRPickingWorkbenchWave_ds.getFirst(true) ?
        MCRPickingWorkbenchWave_ds.getFirst(true): MCRPickingWorkbenchWave_ds.cursor();
    mcrPickingWorkbenchWaveLocal;  mcrPickingWorkbenchWaveLocal = MCRPickingWorkbenchWave_ds.getNext())
    {
        MCRPickingWorkbench::printPickWave(mcrPickingWorkbenchWaveLocal);
    }

    MCRPickingWorkbenchWave_ds.executeQuery();

    if (MCRPickingWorkbench::updateSessionPrintStatus(MCRPickingWorkbenchSession))
    {
        MCRPickingWorkbenchSession_ds.research(true);
    }
}

public int selectionChange()
{
    int ret;

    ret = super();

    // Update the range on the MCRPickingWorkbenchWave datasource
    // query depending on the value of the combobox.
    if (this.valueStr() == "@SYS14437") // all
    {
        waveTypeRange.value("");
    }
    else if (this.valueStr() == "@SYS11408") // created
    {
        waveTypeRange.value(queryValue(MCRPickingWaveStatus::Created));
    }
    else if (this.valueStr() == "@SYS5308") // printed
    {
        waveTypeRange.value(queryValue(MCRPickingWaveStatus::Printed));
    }
    else if (this.valueStr() == "@SYS54623") // completed
    {
        waveTypeRange.value(queryValue(MCRPickingWaveStatus::Completed));
    }

    MCRPickingWorkbenchWave_ds.executeQuery();
    return ret;
}

void clicked()
{
    Args        args;
    FormRun     formRun;

    args = new Args();
    args.record(WMSPickingRoute::find(WMSPickingRoute.PickingRouteID));
    args.caller(element.form());
    formrun = new MenuFunction(menuitemDisplayStr(WMSPickingRegistration), MenuItemType::Display).create(args);
    formrun.run();
    formrun.wait();

    WMSOrderTrans_ds.reread();
    WMSOrderTrans_ds.refresh();
    WMSOrderTrans_ds.active();
    // Removed super
}
