/// <summary>
/// Initializes the form values and form design.
/// </summary>
public void init()
{
    if (element.args().record())
    {
        priceDiscGroup = element.args().record();
    }
    else
    {
        throw error("@MCR36040");
    }
    super();
    if (element.args().parm() == 'Disable')
    {
        MCRPriceDiscGroupItem_ds.allowCreate(false);
        MCRPriceDiscGroupItem_ds.allowDelete(false);
        MCRPriceDiscGroupItem_ds.allowEdit(false);
        InventDim_ds.allowCreate(false);
        InventDim_ds.allowDelete(false);
        InventDim_ds.allowEdit(false);
        AddToSelected.enabled(false);
        RemoveFromSelected.enabled(false);
    }

    // Invent dim fields.
    element.updateDesign(InventDimFormDesignUpdate::Init);
    element.setDimensionFields();
}

/// <summary>
/// Initializes the object used to control the item category tree object.
/// </summary>
void initializeHelper_Item()
{
    EcoResCategoryLookupParameters  lookupParameters = new EcoResCategoryLookupParameters(false, false, false);
    EcoResCategory                  root;

    currentItemHierarchy = EcoResCategoryHierarchy::find(CategoryHierarchyReferenceGroup.value());

    if (currentItemHierarchy)
    {
        root = EcoResCategory::getRoot(currentItemHierarchy.RecId);

        ecoResCategoryTreeDatasource = new EcoResCategoryTreeDatasource( ecoResCategory_ds, InventTree,
                                                                            fieldNum(EcoResCategory, RecId),
                                                                            fieldNum(EcoResCategory, ParentCategory),
                                                                            fieldNum(EcoResCategory, Name),
                                                                            false,
                                                                            true,
                                                                            fieldNum(EcoResCategory, Code),
                                                                            currentItemHierarchy,
                                                                            null,
                                                                            lookupParameters,
                                                                            fieldNum(EcoResCategory, NestedSetLeft) );

        ecoResCategoryTreeDatasource.initRoot(root.Name, root.RecId, ecoResCategoryTreeDatasource.image() );
    }
}

