void setDimensionFields()
{
    InventoryDimensions.allowEdit(true);
    InventoryDimensions.enabled(true);
}

/// <summary>
/// Hides and shows the inventory dimension fields on an item.
/// </summary>
/// <param name="mode">
/// The form design update mode for inventory dimensions.
/// </param>
void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_ProductGroup::newFromForm(element);
            }
        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(MCRPriceDiscGroupItem.ItemID));
            inventDimFormSetup.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(MCRPriceDiscGroupItem.ItemID));
            inventDimFormSetup.formSetControls(false);
            break;
        default: throw error(strfmt("@SYS54195", funcname()));
    }
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void init()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;

    queryBuildDataSource = query.addDataSource(tableNum(MCRPriceDiscGroupItem));
    queryBuildDataSource.addRange(fieldnum(MCRPriceDiscGroupItem, module)).value(queryValue(enum2int(priceDiscGroup.Module)));
    queryBuildDataSource.addRange(fieldnum(MCRPriceDiscGroupItem, type)).value(queryValue(enum2int(priceDiscGroup.type)));
    queryBuildDataSource.addRange(fieldnum(MCRPriceDiscGroupItem, groupID)).value(priceDiscGroup.GroupId);

    this.query(query);

    super();
}

public void selectionChanged()
{
    super();

    ecoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory))
        .clearRange(fieldNum(EcoResCategory, CategoryHierarchy));

    ecoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory))
        .clearRange(fieldNum(EcoResCategory, NestedSetLeft));

    ecoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory))
        .clearRange(fieldNum(EcoResCategory, NestedSetRight));

    // Apply the query ranges.
    ecoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory))
        .addRange(fieldNum(EcoResCategory, CategoryHierarchy))
        .value(queryValue(ecoResCategory.CategoryHierarchy));

    ecoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory))
        .addRange(fieldNum(EcoResCategory, NestedSetLeft))
        .value(Global::queryRange(ecoResCategory.NestedSetLeft, ecoResCategory.NestedSetRight));

    ecoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory))
        .addRange(fieldNum(EcoResCategory, NestedSetRight))
        .value(Global::queryRange(ecoResCategory.NestedSetLeft, ecoResCategory.NestedSetRight));

    ecoResProduct_ds.executeQuery();
}

/// <summary>
/// Adds the selected category product to the fund.
/// </summary>
/// <remarks>
/// If the selected category product has not been released to
/// the current company it cannot be added to the fund.
/// </remarks>
void clicked()
{
    MCRPriceDiscGroupItem  localMCRPriceDiscGroupItem;
    EcoResProduct           ecoResProductLocal;
    InventDimCombination    inventDimCombo;
    InventTable             inventTableLocal;

    if (currentItemHierarchy)
    {
        for (ecoResProductLocal = EcoResProduct_ds.getFirst(true) ? EcoResProduct_ds.getFirst(true): EcoResProduct_ds.cursor();
                ecoResProductLocal;
                ecoResProductLocal = EcoResProduct_ds.getnext())
        {
            inventTableLocal = InventTable::findByProduct(ecoResProductLocal.RecId);
            localMCRPriceDiscGroupItem.ItemID = inventTableLocal.ItemId;
            localMCRPriceDiscGroupItem.GroupId = priceDiscGroup.GroupId;
            localMCRPriceDiscGroupItem.Type = priceDiscGroup.Type;
            localMCRPriceDiscGroupItem.UnitID = inventTableLocal.inventUnitId();

            if (localMCRPriceDiscGroupItem.ItemID)
            {
                select RecId from inventDimCombo
                    where inventDimCombo.ItemId == localMCRPriceDiscGroupItem.ItemID;

                if (inventDimCombo)
                {
                    inventDimCombo.clear();
                    while select InventDimID from inventDimCombo
                        where inventDimCombo.ItemId == localMCRPriceDiscGroupItem.ItemID
                    {
                        localMCRPriceDiscGroupItem.InventDimID = inventDimCombo.InventDimId;
                        if (!MCRPriceDiscGroupItem::findGroupItem(ModuleInventCustVend::Invent, PriceGroupType::LineDiscGroup, MCRPriceDiscGroupItem.GroupId, localMCRPriceDiscGroupItem.itemid))
                        {
                            localMCRPriceDiscGroupItem.insert();
                        }
                    }
                }
                else
                {
                    localMCRPriceDiscGroupItem.InventDimID = InventDim::findOrCreateBlank().inventDimID;
                    if (!MCRPriceDiscGroupItem::findGroupItem(ModuleInventCustVend::Invent, PriceGroupType::LineDiscGroup, MCRPriceDiscGroupItem.GroupId, localMCRPriceDiscGroupItem.itemid))
                    {
                        localMCRPriceDiscGroupItem.insert();
                    }
                }
                MCRPriceDiscGroupItem_ds.executeQuery();
            }
            else
            {
                error(strFmt("@MCR38748", EcoResProduct::find(ecoResProductLocal.RecId).DisplayProductNumber));
            }
        }
    }
}

//// <summary>
/// Removes the selected item from the current fund.
/// </summary>
void clicked()
{
    MCRPriceDiscGroupItem   localMCRPriceDiscGroupItem;

    for (localMCRPriceDiscGroupItem = MCRPriceDiscGroupItem_ds.getFirst(true) ? MCRPriceDiscGroupItem_ds.getFirst(true): MCRPriceDiscGroupItem_ds.cursor();  localMCRPriceDiscGroupItem;
            localMCRPriceDiscGroupItem = MCRPriceDiscGroupItem_ds.getnext())
    {
        localMCRPriceDiscGroupItem.delete();
    }
    MCRPriceDiscGroupItem_ds.executeQuery();
}

