[Form]
public class MCRPriceHistory extends FormRun
{
    MCRPriceHistoryForm     mcrPriceHistoryForm;
    SalesParameters         salesParameters;
    PurchParameters         purchParameters;
    RecId                   activeRecId;
    boolean                 isPdsRebateEnabled;
    boolean                 isTAMVendorRebateEnabled;

}

private Price calcTotalVendorRebate()
{
    Price               totalVendorRebate;
    TAMVendRebateTable  tamVendRebateTableTmp;

    if (isTAMVendorRebateEnabled)
    {
        tamVendRebateTableTmp.setTmp();
        tamVendRebateTableTmp.setTmpData(tamVendRebateTable);
        select sum(StartingRebateAmt) from tamVendRebateTableTmp;
        totalVendorRebate = tamVendRebateTableTmp.StartingRebateAmt;
    }

    return totalVendorRebate;
}

/// <summary>
///     Valdiates the buffer type and intializes the form.
/// </summary>
public void init()
{
    // Validate passed in record is of right type
    if (! element.args()
        || ! (element.args().dataset()    == tableNum(PurchLine)
              || element.args().dataset() == tableNum(SalesLine)
              || element.args().dataset() == tableNum(SalesQuotationLine)))
    {
        throw error(strFmt("@MCR35593",
                           element.name(),
                           tableStr(PurchLine),
                           tableStr(SalesLine),
                           tableStr(SalesQuotationLine)));
    }

    isPdsRebateEnabled = isConfigurationkeyEnabled(configurationKeyNum(PDSRebate));
    isTAMVendorRebateEnabled = isConfigurationkeyEnabled(configurationKeyNum(TAMVendorRebate));

    super();

    if (element.args().dataset() == tableNum(SalesLine))
    {
        SalesLine salesLine = element.args().record();

        isPdsRebateEnabled = isPdsRebateEnabled && !salesLine.PdsExcludeFromRebate;
    }

    if (isPdsRebateEnabled)
    {
        pdsRebateTable_ds.allowCheck(true);
    }
}

/// <summary>
/// Shows or hides controls depending on the type of price history line.  Updates values
/// of price controls such as price, discount amount, discount percentage, and line amount.
/// Displays updated margin estimation calculations.
/// </summary>
/// <remarks>
/// Use the <c>MCRPriceHistoryForm</c> object to retrieve values.
/// </remarks>
public void setControls()
{
    MCRMarginAlert  mcrMarginAlert;
    RepShowZero     repShowZero;
    boolean         agreementIsVolumeBased;
    boolean         showVendRebates;
    boolean         showCustRebates;
    RecId           currentRecId;

    currentRecId = element.args().record().RecId;

    if (activeRecId != currentRecId)
    {
        activeRecId = currentRecId;
        mcrPriceHistoryForm = MCRPriceHistoryForm::construct(element.args().record());

        delete_from mcrPriceHistory;
        mcrPriceHistory_ds.executeQuery();

        delete_from mcrPriceHistoryPotential;
        mcrPriceHistoryPotential_ds.executeQuery();

        delete_from pdsRebateTable;
        pdsRebateTable_ds.executeQuery();

        delete_from tamVendRebateTable;
        tamVendRebateTable_ds.executeQuery();

        delete_from mcrRoyaltyVendTable;
        mcrRoyaltyVendTable_ds.executeQuery();

        delete_from tmpMCRAgreementLine;
        tmpMCRAgreementLine_ds.executeQuery();

        showVendRebates = mcrPriceHistoryForm.showVendRebates();
        showCustRebates = mcrPriceHistoryForm.showCustRebates();

        // Show/hide margin controls
        groupMarginEstimation.visible(mcrPriceHistoryForm.showMarginRelated());
        marginVendorRebate.visible(showVendRebates);
        marginRebate.visible(showCustRebates);
        marginRoyalty.visible(mcrPriceHistoryForm.showMarginRoyaltyAmount());
        marginBroker.visible(mcrPriceHistoryForm.showMarginBrokerAmount());
        marginEstimatedSalesPrice.visible(mcrPriceHistoryForm.showMarginEstimatedSalesPrice());

        if (element.args().dataset() == tableNum(PurchLine))
        {
            marginPrice.helpText("@MCR4410110");
        }

        tabPageVendRebates.visible(showVendRebates);
        tabPagePdsRebates.visible(showCustRebates);

        // Price history form determines whether to hide or show royalty tab
        tabPageRoyalty.visible(mcrPriceHistoryForm.showRoyaltyTab());

        // In some cases agreements are not applicable.
        tabPageAgreement.visible(mcrPriceHistoryForm.showAgreementRelated());

        // Item and price
        itemId.text(mcrPriceHistoryForm.getItemId());
        itemName.text(InventTable::find(mcrPriceHistoryForm.getItemId()).itemName());
        qty.realValue(mcrPriceHistoryForm.getQty());
        unitId.text(mcrPriceHistoryForm.getUnitId());
        priceUnit.realValue(mcrPriceHistoryForm.getPriceUnit());
        currencyCode.text(mcrPriceHistoryForm.getCurrencyCode());

        // Pricing exceptional cases
        override.value(mcrPriceHistoryForm.isOverride());

        // Discount combination method
        lineMultiline.text(enum2str(mcrPriceHistoryForm.getLineMultiLn()));

        // Discount amount combination
        lineDiscAmount.realValue(mcrPriceHistoryForm.getLineDiscAmount());
        multilineDiscAmount.realValue(mcrPriceHistoryForm.getMultilineDiscAmount());

        // Discount percent combination
        lineDiscPct.realValue(mcrPriceHistoryForm.getLineDiscPct());
        multilineDiscPct.realValue(mcrPriceHistoryForm.getMultilineDiscPct());

        // Calculation trade price
        unitPrice.realValue(mcrPriceHistoryForm.getUnitPriceExtendedUnitPrice());
        unitPriceNet.realValue(mcrPriceHistoryForm.getUnitPriceExtendedAmount());

        // Calculation discount amount
        discAmount.realValue(mcrPriceHistoryForm.getUnitPriceExtendedUnitPrice() - mcrPriceHistoryForm.getDiscAmountExtendedUnitPrice());
        discAmountNet.realValue(mcrPriceHistoryForm.getUnitPriceExtendedAmount() - mcrPriceHistoryForm.getDiscAmountExtendedAmount());

        // Calculation markup
        markup.realValue(mcrPriceHistoryForm.getMarkupPerUnit());
        markupNet.realValue(mcrPriceHistoryForm.getMarkup());

        subTotalUnitPrice.realValue(mcrPriceHistoryForm.getMarkupExtendedUnitPrice());
        subTotalNet.realValue(mcrPriceHistoryForm.getMarkupExtendedAmount());

        // Calculation discount percent
        DiscountPercentLabel.text(strfmt("@SCM:DiscountPercentage", mcrPriceHistoryForm.getDiscPct()));
        discPercentage.realValue(mcrPriceHistoryForm.getMultilineDiscPercentage());
        discPercentageNet.realValue(mcrPriceHistoryForm.getMultilineDiscPercentageNet());

        totalUnitPrice.realValue(mcrPriceHistoryForm.getDiscPctExtendedUnitPrice());
        totalNetPrice.realValue(mcrPriceHistoryForm.getDiscPctExtendedAmount());

        // Margin calculations
        if (mcrPriceHistoryForm.showMarginRelated())
        {
            mcrMarginCurrencyCode.text(CompanyInfoHelper::standardCurrency());

            // If the quantity is 0 or negative, then margin controls should be blank and not show 0.
            if (mcrPriceHistoryForm.getQty() <= 0)
            {
                repShowZero = RepShowZero::No;
            }
            else
            {
                repShowZero = RepShowZero::Yes;
            }

            marginExtendedUnitPrice.showZero(repShowZero);
            marginVendorRebate.showZero(repShowZero);
            marginRebate.showZero(repShowZero);
            marginRoyalty.showZero(repShowZero);
            marginBroker.showZero(repShowZero);
            marginEstimatedSalesPrice.showZero(repShowZero);
            marginPrice.showZero(repShowZero);

            mcrMarginAlert = mcrPriceHistoryForm.mcrMarginAlert();
            mcrMarginAlert.parmTotalVendorRebate(this.calcTotalVendorRebate());
            mcrMarginAlert.calcMargin();
            mcrMarginAlert.persistMarginCalculation();

            marginExtendedUnitPrice.realValue(mcrMarginAlert.parmExtendedUnitPrice());
            marginVendorRebate.realValue(mcrMarginAlert.parmVendRebateAmountPerUnit());
            marginRebate.realValue(mcrMarginAlert.parmCustRebateAmountPerUnit());
            marginRoyalty.realValue(mcrMarginAlert.parmRoyaltyAmountPerUnit());
            marginBroker.realValue(mcrMarginAlert.parmBrokerAmountPerUnit());
            marginEstimatedSalesPrice.realValue(mcrMarginAlert.parmEstimatedSalesPrice());
            marginPrice.realValue(mcrMarginAlert.parmMarginPrice());
            estimatedMarginPercentStr.text(mcrMarginAlert.parmMCRMarginPercentStr());
            marginIcon.normalImage(MCRMarginAlert::getMarginSymbol(mcrMarginAlert.parmMarginPercent()));
        }

        // In some cases agreements are not applicable.
        if (mcrPriceHistoryForm.showAgreementRelated())
        {
            if (tmpMCRAgreementLine.RecId
                && tmpMCRAgreementLine.AgreementLineType == CommitmentType::ProductVolume)
            {
                agreementIsVolumeBased = true;
            }
            agreementAmount.visible(agreementIsVolumeBased);
            agreementQuantity.visible(! agreementIsVolumeBased);
        }
    }
}

/// <summary>
/// Opens trade promotion for a given merchandising event ID.
/// </summary>
/// <param name = "_merchandisingEvent">Merchandising event ID to open trade promotion for.</param>
public void jumpRefTAMTradePromotion(MCRMerchandisingEventID _merchandisingEvent)
{
    TAMTradePromotion tamTradePromotion = TAMTradePromotion::findByPromoID(mcrPriceHistoryForm.displayPromotionId(_merchandisingEvent));

    Args args = new Args();
    args.parmEnumeration(TAMPromotionMode::Customer);
    args.record(tamTradePromotion);

    FormRun formRun = new MenuFunction(menuitemDisplayStr(TAMPromotionsManagmentMenu), MenuItemType::Display).create(args);
    formRun.run();
    formRun.wait();
}

public void linkActive()
{
    super();

    element.setControls();

    if (mcrPriceHistoryForm)
    {
        inventDimHeader.data(mcrPriceHistoryForm.getInventDim());
    }
}

