display PriceUnit displayPriceUnit(MCRPriceHistory _mcrPriceHistory)
{
    PriceUnit priceUnitLocal;
    // Price unit only applicable for price (not discount)
    if (_mcrPriceHistory.PriceHistoryType == MCRPriceHistoryType::TradePrice)
    {
        priceUnitLocal = _mcrPriceHistory.priceUnit();
    }
    return priceUnitLocal;
}

