public void executeQuery()
{
    if (mcrPriceHistoryForm)
    {
        mcrPriceHistoryForm.initTmpMCRAgreementLine(tmpMCRAgreementLine);
        if (tmpMCRAgreementLine.AgreementId)
        {
            tmpMCRAgreementLine.insert();
        }
    }
    super();
}

public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange      queryBuildRange;

    if (mcrPriceHistoryForm
        && isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        queryBuildDataSource = retailSalesQuotationDiscountLineView_ds.queryBuildDataSource();
        queryBuildRange = queryBuildDataSource.findRange(fieldNum(RetailSalesQuotationDiscountLineView, InventTransId));
        queryBuildRange.value(queryValue(mcrPriceHistoryForm.getInventTransId()));

        super();
    }
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        queryBuildDataSource = retailSalesQuotationDiscountLineView_ds.queryBuildDataSource();
        queryBuildDataSource.clearDynalinks();
        queryBuildDataSource.addRange(fieldNum(RetailSalesQuotationDiscountLineView, InventTransId));
    }
}

public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange      queryBuildRange;

    if (mcrPriceHistoryForm
        && isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        // get the query datasource and select all records with inventory transaction ID.
        queryBuildDataSource = retailSalesPriceAdjustmentLineView_ds.queryBuildDataSource();
        queryBuildRange = queryBuildDataSource.findRange(fieldNum(RetailSalesPriceAdjustmentLineView, InventTransId));
        queryBuildRange.value(queryValue(mcrPriceHistoryForm.getInventTransId()));

        super();
    }
}

public void init()
