    element.jumpRefTAMTradePromotion(mcrPriceHistory.MerchandisingEventID);
}

public void jumpRef()
{
    element.jumpRefTAMTradePromotion(mcrPriceHistoryPotential.MerchandisingEventID);
}

public void jumpRef()
{
    Args args = new Args();            
    FormRun formRun;

    if (RetailSalesQuotationDiscountLineView.DiscountOriginType == RetailDiscountOriginType::Periodic)
    {
        args.parmEnumType(enumNum(RetailDiscountOfferTypeBase));
        args.parmEnum(RetailPeriodicDiscount::findByOfferId(RetailSalesQuotationDiscountLineView.PeriodicDiscountOfferId).PeriodicDiscountType);
        args.parm(RetailSalesQuotationDiscountLineView.PeriodicDiscountOfferId);
        formRun = new MenuFunction(menuitemDisplayStr(RetailDiscountOffer), MenuItemType::Display).create(args);
    }

    if (RetailSalesQuotationDiscountLineView.DiscountOriginType == RetailDiscountOriginType::Tender)
    {
        args.parm(RetailSalesQuotationDiscountLineView.PeriodicDiscountOfferId);
        formRun = new MenuFunction(menuitemDisplayStr(RetailTenderDiscount), MenuItemType::Display).create(args);
    }

    formRun.run();
    formRun.wait();
}

public void jumpRef()
{
    Args args = new Args();
    args.parmEnumType(enumNum(RetailDiscountOfferTypeBase));
    args.parmEnum(RetailPeriodicDiscount::findByOfferId(RetailSalesPriceAdjustmentLineView.OfferId).PeriodicDiscountType);
    args.parm(RetailSalesPriceAdjustmentLineView.OfferId);

    FormRun formRun = new MenuFunction(menuitemDisplayStr(RetailDiscountOffer), MenuItemType::Display).create(args);
    formRun.run();
    formRun.wait();
}
