Method.Source:
    [Form]
    public class MCRRFMDefinition extends FormRun
    {
}

public void init()
{
    super();
}

void setDefinitionControls()
{
    // recency definition
    MCRRecencyDefinition_ds.object(fieldNum(MCRRecencyDefinition, FrequencyScore)).visible(MCRRFMDefinition.FrequencyPrecedence > MCRRFMDefinition.RecencyPrecedence);
    MCRRecencyDefinition_ds.object(fieldNum(MCRRecencyDefinition, MonetaryScore)).visible(MCRRFMDefinition.MonetaryPrecedence > MCRRFMDefinition.RecencyPrecedence);

    // frequency definition
    MCRFrequencyDefinition_ds.object(fieldNum(MCRFrequencyDefinition, RecencyScore)).visible(MCRRFMDefinition.RecencyPrecedence > MCRRFMDefinition.FrequencyPrecedence);
    MCRFrequencyDefinition_ds.object(fieldNum(MCRFrequencyDefinition, MonetaryScore)).visible(MCRRFMDefinition.MonetaryPrecedence > MCRRFMDefinition.FrequencyPrecedence);

    // monetary definition
    MCRMonetaryDefinition_ds.object(fieldNum(MCRMonetaryDefinition, RecencyScore)).visible(MCRRFMDefinition.RecencyPrecedence > MCRRFMDefinition.MonetaryPrecedence);
    MCRMonetaryDefinition_ds.object(fieldNum(MCRMonetaryDefinition, FrequencyScore)).visible(MCRRFMDefinition.FrequencyPrecedence > MCRRFMDefinition.MonetaryPrecedence);

    MCRRecencyDefinition_ds.allowEdit(MCRRFMDefinition.EvenDistribution == NoYes::No);
    MCRFrequencyDefinition_ds.allowEdit(MCRRFMDefinition.EvenDistribution == NoYes::No);
    MCRMonetaryDefinition_ds.allowEdit(MCRRFMDefinition.EvenDistribution == NoYes::No);

    ButtonRefreshRFM.enabled(MCRRFMDefinition.EvenDistribution == NoYes::No);
}

public int active()
{
    int ret;

    ret = super();

    element.setDefinitionControls();

    return ret;
}

public void write()
{
    super();

    element.setDefinitionControls();

    MCRRFMDefinition.reread();
    MCRRecencyDefinition_ds.executeQuery();
    MCRFrequencyDefinition_ds.executeQuery();
    MCRMonetaryDefinition_ds.executeQuery();
}

public void write()
{
    super();

    this.executeQuery();
}

public int active()
{
    int         ret;
    MCRScore     highScore = MCRRFMDefinition.RecencyDivisions * MCRRFMDefinition.RecencyMultiplier;
    MCRScore     lowScore = MCRRFMDefinition.RecencyMultiplier;

    ret = super();

    this.object(fieldNum(MCRRecencyDefinition, RecencyStart)).enabled(MCRRecencyDefinition.Score  != highScore);
    this.object(fieldNum(MCRRecencyDefinition, RecencyEnd)).enabled(MCRRecencyDefinition.Score  != lowScore);

    return ret;
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(MCRRecencyDefinition)).addSortField(fieldNum(MCRRecencyDefinition, FrequencyScore), SortOrder::Descending);
    this.query().dataSourceTable(tableNum(MCRRecencyDefinition)).addSortField(fieldNum(MCRRecencyDefinition, MonetaryScore), SortOrder::Descending);
    this.query().dataSourceTable(tableNum(MCRRecencyDefinition)).addSortField(fieldNum(MCRRecencyDefinition, Score), SortOrder::Descending);
}

public display MCRRecencyDatePart mcrRecencyDatePart(MCRRecencyDefinition _recencyDefinition)
{
    MCRRFMDefinition    rfmDefinition = MCRRFMDefinition::find(_recencyDefinition.RFMDefinitionName);

    return rfmDefinition.RecencyDatePart;
}

