void clicked()
{
    MCRRFM   rfm;
    super();

    rfm = MCRRFM::construct(MCRRFMDefinition.RFMDefinitionName);

    rfm.calculateRFM();

    MCRRFMDefinition_ds.refresh();
    MCRRecencyDefinition_ds.executeQuery();
    MCRFrequencyDefinition_ds.executeQuery();
    MCRMonetaryDefinition_ds.executeQuery();
}

void clicked()
{
    MCRRFM   rfm;
    super();

    rfm = MCRRFM::construct(MCRRFMDefinition.RFMDefinitionName);

    rfm.refreshFairDistRFM();

    MCRRFMDefinition_ds.refresh();
    MCRRecencyDefinition_ds.executeQuery();
    MCRFrequencyDefinition_ds.executeQuery();
    MCRMonetaryDefinition_ds.executeQuery();
}

void clicked()
{
    MCRRFM   rfm;
    MCRRFMDefinition rfmDefinition;
    rfmDefinition = MCRRFMDefinition::find(MCRRFMDefinition.RFMDefinitionName);
    super();

    rfm = MCRRFM::construct(MCRRFMDefinition.RFMDefinitionName);

    rfm.setCustomerRFMScore();

    MCRRFMDefinition_ds.executeQuery();
    MCRRFMDefinition_ds.positionToRecord(rfmDefinition);
    MCRRecencyDefinition_ds.executeQuery();
    MCRFrequencyDefinition_ds.executeQuery();
    MCRMonetaryDefinition_ds.executeQuery();
}

void clicked()
{
    MCRRFM   rfm;
    super();

    rfm = MCRRFM::construct(MCRRFMDefinition.RFMDefinitionName);

    rfm.clearHistory();

    MCRCustRFMScore_ds.executeQuery();
}
