Method.Source:
/// <summary>
///     Prevents checks from being reused if the bank parameters
///     are not setup appropriately.
/// </summary>
public void init()
{
    super();

    if (!BankParameters::find().AllowCheckReuse)
    {
        bankChequeReuse.visible(false);
    }

    allOpenPostedField.selection(AllOpenPosted::Open);
}

/// <summary>
///     Enables the controls accordingly.
/// </summary>
void setControls()
{
    // A check cannot be reused once the batch is posted and cannot be reused if a check has not been created.
    bankChequeReuse.enabled(!ledgerJournalTable.Posted && ledgerJournalTrans.PaymentStatus == CustVendPaymStatus::Sent);

    // Don't allow posting if its already been posted.
    postBatch.enabled(!ledgerJournalTable.Posted);

    // Don't allow check printing once the batch is posted.
    printBatch.enabled(!ledgerJournalTable.Posted);
}

public void executeQuery()
{
    QueryBuildDataSource qbdsLedgerJournalTable;

    qbdsLedgerJournalTable = this.query().dataSourceName(ledgerJournalTable_ds.name());
    qbdsLedgerJournalTable.clearRanges();

    if (allOpenPostedField.valueStr() == enum2str(AllOpenPosted::Open))
    {
        qbdsLedgerJournalTable.addRange(fieldNum(LedgerJournalTable, Posted)).value(SysQuery::value(NoYes::No));
    }
    else if (allOpenPostedField.valueStr() == enum2str(AllOpenPosted::Posted))
    {
        qbdsLedgerJournalTable.addRange(fieldNum(LedgerJournalTable, Posted)).value(enum2Value(NoYes::Yes));
    }
    else
    {
        qbdsLedgerJournalTable.addRange(fieldNum(LedgerJournalTable, Posted)).value(SysQuery::valueUnlimited());
    }

    super();
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(MCRRCTransBatchTable, checkBatchTotal));
    this.cacheAddMethod(tableMethodStr(MCRRCTransBatchTable, displayTransactionCurrency));
}

public int active()
{
    int ret;

    ret = super();

    element.setControls();

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.setControls();

    return ret;
}

void clicked()
{
    try
    {
        JournalTableData::newTable(ledgerJournalTable).updateBlock(JournalBlockLevel::None, JournalBlockLevel::InUse);

        MCRRefundCheckBatch::printCheckBatchToSelectedPrinter(mcrrcTransBatchTable, UserInteractive::Yes);
    }
    finally
    {
        JournalTableData::newTable(ledgerJournalTable).updateBlock(JournalBlockLevel::InUse,JournalBlockLevel::None);
    }

    mcrrcTransBatchTable_ds.refresh();
    ledgerJournalTrans_ds.research();

    element.setControls();
}

void clicked()
{
    try
    {
        JournalTableData::newTable(ledgerJournalTable).updateBlock(JournalBlockLevel::None, JournalBlockLevel::InUse);

        MCRRefundCheckBatch::postCheckBatch(mcrrcTransBatchTable);
    }
    finally
    {
        JournalTableData::newTable(ledgerJournalTable).updateBlock(JournalBlockLevel::InUse,JournalBlockLevel::None);
    }

    mcrrcTransBatchTable_ds.refresh();
    ledgerJournalTable.reread();
    ledgerJournalTrans_ds.research();

    element.setControls();
}

int selectionChange()
{
    int ret;

    ret = super();

    mcrrcTransBatchTable_ds.executeQuery();

    return ret;
}

void clicked()
{
    LedgerJournalTrans  ledgerJournalTransRecord;
    LedgerJournalTrans  ledgerJournalTransLocal;
    try
    {
        JournalTableData::newTable(ledgerJournalTable).updateBlock(JournalBlockLevel::None, JournalBlockLevel::InUse);

        // NOTE:  if only 1 row is selected in the grid, the row is not marked
        if (!ledgerJournalTrans_ds.anyMarked())
        {
            ledgerJournalTrans_ds.mark(1);
        }
        ledgerJournalTransLocal = ledgerJournalTrans_ds.getFirst(1);

        // Loop through the selected records
        while (ledgerJournalTransLocal)
        {
            if (ledgerJournalTransLocal.PaymentStatus == CustVendPaymStatus::Sent)
            {
                ledgerJournalTransRecord.reuseCheck(ledgerJournalTransLocal);
            }

            ledgerJournalTransLocal = ledgerJournalTrans_ds.getNext();
        }
    }
    finally
    {
        JournalTableData::newTable(ledgerJournalTable).updateBlock(JournalBlockLevel::InUse,JournalBlockLevel::None);
        ledgerJournalTrans_ds.research();
    }
}
