Method.Source:
[Form]
public class MCRReturnRecap extends FormRun
{
    SalesTableType_ReturnItem   salesTableType_ReturnItem;

    boolean                     cameFromReturnInquiryForm;
    SalesTable                  salesTable_Return;
}

public void init()
{
    Args            args;
    super();

    args = element.args();

    // When called from the inquiry form don't allow
    // the user to update the return related data.
    if (args.parm() == 'Inquiry')
    {
        cameFromReturnInquiryForm = true;
        SubmitButton.visible(false);
        MCRDeleteOrderExchange.visible(false);
        MCRDeleteOrderReturn.visible(false);
    }
    else
    {
        cameFromReturnInquiryForm = false;
        SubmitButton.visible(true);
        MCRDeleteOrderExchange.visible(true);
        MCRDeleteOrderReturn.visible(true);
    }

    // The return table record must be passed.
    if (args.dataset() == tableNum(SalesTable))
    {
        salesTable_Return = element.args().record();

        salesTableType_ReturnItem = new SalesTableType_ReturnItem(salesTable_Return);

        if (!cameFromReturnInquiryForm)
        {
            salesTableType_ReturnItem.mcrValidateComplete();
        }
    }
    else
    {
        throw Exception::Error;
    }

    AcceptDeclineButtonExchange.visible(false);

    // If there's a related exchange order for the return then show the exchange group.
    if (!salesTable_Return.ReturnReplacementId)
    {
        this.SetExchangeControls(false);
    }
}

/// <summary>
///     Sets the visibility of controls.
/// </summary>
void setExchangePaymControls()
{
    boolean     multiplePmtsExist = false;
    if (SalesTableReturn.ReturnReplacementId)
    {
        if (SalesTableExchange && MCRCustPaymTable::count(SalesTableExchange.TableId, SalesTableExchange.RecId) > 1)
        {
            multiplePmtsExist = true;
        }

        MultiplePaymGroupExchange.visible(multiplePmtsExist);
        SinglePaymGroupExchange.visible(!multiplePmtsExist);

        if (!multiplePmtsExist)
        {
            CheckPaymGroupExchange.visible(MCRCustPaymTableExchange.CustPaymType == MCRCustPaymType::Check);
            CreditCardPaymGroupExchange.visible(MCRCustPaymTableExchange.CustPaymType == MCRCustPaymType::CreditCard);
            GiftCardGroupExchange.visible(MCRCustPaymTableExchange.CustPaymType == MCRCustPaymType::GiftCard);
        }
    }
}

/// <summary>
///     Sets the total fields on the form for the exchange if it exists.
/// </summary>
void setExchangeTotal()
{
    MCRSalesOrderTotals salesOrderTotalsExchange;

    if (SalesTableReturn.ReturnReplacementId)
    {
        MCRReturnSalesOrderExchange::calculateTieredChargesForExchange(SalesTableExchange);

        salesOrderTotalsExchange = new MCRSalesOrderTotals(SalesTableExchange);

        ExchangeTotal.realValue(salesOrderTotalsExchange.getTotalProduct());
        ExchangeDiscount.realValue(salesOrderTotalsExchange.getTotalDisc());

        ExchangeMiscCharge.realValue(salesOrderTotalsExchange.getTotalMarkup());
        ExchangeTax.realValue(salesOrderTotalsExchange.getTotalTax());
        ExchangeNetTotal.realValue(salesOrderTotalsExchange.getTotalOrder());
    }
    else
    {
        ExchangeTotal.realValue(0.0);
        ExchangeDiscount.realValue(0);
        ExchangeMiscCharge.realValue(0);
        ExchangeTax.realValue(0);
        ExchangeNetTotal.realValue(0);
    }
}

/// <summary>
///     Sets the properties for the payment controls.
/// </summary>
void setReturnPaymControls()
{
    boolean     multiplePmtsExist = false;

    if (SalesTableReturn && MCRCustPaymTable::count(SalesTableReturn.TableId, SalesTableReturn.RecId) > 1)
    {
        multiplePmtsExist = true;
    }

    MultiplePaymGroupReturn.visible(multiplePmtsExist);
    SinglePaymGroupReturn.visible(!multiplePmtsExist);

    if (!multiplePmtsExist)
    {
        CheckPaymGroupReturn.visible(MCRCustPaymTableReturn.CustPaymType == MCRCustPaymType::Check);
        CreditCardPaymGroupReturn.visible(MCRCustPaymTableReturn.CustPaymType == MCRCustPaymType::CreditCard);
        GiftCardGroupReturn.visible(MCRCustPaymTableReturn.CustPaymType == MCRCustPaymType::GiftCard);
    }
}

/// <summary>
///     Sets the total fields on the form for the return.
/// </summary>
public void setReturnTotals()
{
    if (!SalesTableReturn.SalesId)
    {
        ReturnsTotal.realValue(0);
        ReturnDiscount.realValue(0);
        ReturnsMiscCharge.realValue(0);
        ReturnsTax.realValue(0);
        ReturnNetTotal.realValue(0);
        ReturnBalance.realValue(0);

        return;
    }

    MCRSalesOrderTotals salesOrderTotalsReturn = element.getSalesOrderTotals();
    ReturnsTotal.realValue(salesOrderTotalsReturn.getTotalProduct());
    ReturnDiscount.realValue(salesOrderTotalsReturn.getTotalDisc());
    ReturnsMiscCharge.realValue(salesOrderTotalsReturn.getTotalMarkup());
    ReturnsTax.realValue(salesOrderTotalsReturn.getTotalTax());
    ReturnNetTotal.realValue(salesOrderTotalsReturn.getTotalOrder());
    ReturnBalance.realValue(RetailPaymentsReturnOrderTotalsHelper::calculateBalance(salesOrderTotalsReturn, SalesTableReturn));
}

[Replaceable]
protected MCRSalesOrderTotals getSalesOrderTotals()
{
    return RetailPaymentsReturnOrderTotalsHelper::calculateReturnOrderTotals(SalesTableReturn, salesTableType_ReturnItem);
}

private void SetExchangeControls(boolean isVisible = false)
{
    ExchangeTab.visible(isVisible);
    ExchangeAmounts.visible(isVisible);
    ExchangePayments.visible(isVisible);
    ModifyButtonExchange.visible(isVisible);
}

public void executeQuery()
{
    super();

    // Ensure payments don't get reset on inquiry
    if (element.args().parm()  != 'Inquiry')
    {
        element.setReturnTotals();
    }
    element.setReturnPaymControls();

    if (SalesTableReturn.ReturnReplacementId)
    {
        salesTableExchange_ds.executeQuery();
    }
    else
    {
        element.SetExchangeControls(false);
    }
}

public void executeQuery()
{
    QueryBuildDatasource qbdsSalesTableExchange;
    qbdsSalesTableExchange = this.query().dataSourceNo(1);
    qbdsSalesTableExchange.clearRanges();
    qbdsSalesTableExchange.clearDynalinks();

    if (SalesTableReturn.ReturnReplacementId)
    {
        qbdsSalesTableExchange.addRange(fieldNum(SalesTable, SalesId)).value(SalesTableReturn.ReturnReplacementId);
    }
    else
    {
        qbdsSalesTableExchange.addRange(fieldNum(SalesTable, SalesId)).value(sysQuery::valueEmptyString());
    }

    super();

    // Ensure payments don't get reset on inquiry
    if (element.args().parm()  != 'Inquiry')
    {
        element.setExchangeTotal();
    }
    element.setExchangePaymControls();
}

public void init()
{
    super();

    MCRCustPaymTableReturn_ds.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayCCNumMasked), false);
}

public void executeQuery()
{
    super();

    element.setReturnPaymControls();
}

