    super();

    MCRCustPaymTableExchange_ds.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayCCNumMasked), false);
}

public void executeQuery()
{
    super();

    element.setExchangePaymControls();
}

/// <summary>
///     Deletes the return and the exchange order.
/// </summary>
void clicked()
{
    FormRun callerForm = element.args().caller();

    ttsBegin;

    if (salesTableReturn.validateDelete())
    {
        SalesTableReturn.delete();
    }

    ttsCommit;

    element.close();
}

void clicked()
{
    element.close();
}

/// <summary>
///     Calls the hold code form to display
///     all holds for the return.
/// </summary>
void clicked()
{
    Args args = new Args();
    FormRun   menuFunction;

    args.record(SalesTableReturn);
    menuFunction = new Menufunction(menuitemDisplayStr(MCRHoldCodeTransSOCreate), menuitemtype::Display).create(args);
    menuFunction.run();
    menuFunction.wait();

    // refresh data
    SalesTableReturn_ds.executeQuery();
}

