/// <summary>
///     Displays the customer payments form, to allow the user to
///     add a payment for the calling record if the user is 
///     authorized to override return payments.
/// </summary>
void clicked()
{
    if (McrReturnOverride_PaymentMethod::mcrAllowReturnWithAlternatePayments(salesTable_Return))
    {
        args paymentArgs;

        FormRun paymentFormRun;

        paymentArgs = new Args();

        paymentArgs.parmEnumType(enumnum(MCRAllowAlternateReturnPayments));
        paymentArgs.parmEnum(NoYes::Yes);

        // Pass calling record to the payment creation form
        paymentArgs.record(element.args().record());

        paymentFormRun = new MenuFunction(menuItemDisplayStr(MCRCustPaym), MenuItemType::Display).create(paymentArgs);

        paymentFormRun.run();
        paymentFormRun.wait();

        MCRCustPaymTableReturn_ds.research(true);
    }
}

