///  Removes the selected payment if the user is authorized to override return payments.
/// </summary>
public void clicked()
{
    if (McrReturnOverride_PaymentMethod::mcrAllowReturnWithAlternatePayments(salesTable_Return))
    {
        super();
    }
}

