/// <summary>
///     Opens the exchange order for editing.
/// </summary>
void clicked()
{
    SalesTable::MCRJumpRef(SalesTableExchange, MCRCustomerServiceParam::Modify, element);

    element.setExchangeTotal();

    if (SalesTableReturn.mcrReturnSalesTable().ApplyCreditToExchange)
    {
        // Re-generate the payment records to adjust
        // the on account (credit) that should be used
        // to pay for the exchage.
        salesTableType_ReturnItem.mcrCreatePayments();
    }

    SalesTableReturn_ds.refresh();
    SalesTableReturn_ds.reread();
}

