/// <summary>
///     Deletes the exchange order.
/// </summary>
void clicked()
{
    salesTableType_ReturnItem.mcrDeleteExchange(true);

    super();

    salesTableReturn.reread();
    salesTableReturn_ds.refresh();

    // Re-instantiate since the data has changed from when
    // initially created (exchange deleted).
    salesTableType_ReturnItem = new SalesTableType_ReturnItem(SalesTableReturn);
    // Re-generate the payment records to remove
    // any on account (credit) that should be refunded
    // to a different pay method.
    salesTableType_ReturnItem.mcrCreatePayments();
}

