/// <summary>
///     Calls end order processing for the exchange order and return order.
/// </summary>
void clicked()
{
    MCRSalesOrderTotals  salesOrderTotalsExchange;
    MCRCustPaymTotals    custPaymTotals;
    MCROrderRecapStatus                 recapStatus;
    SalesTable          salesTable;
    super();

    recapStatus = salesTableType_ReturnItem.mcrComplete();
    salesTable = salesTableReturn.data();

    // Based on the return status, close or leave the form open.
    switch (recapStatus)
    {
        // Close the form.
        case MCROrderRecapStatus::Close:
        case MCROrderRecapStatus::CloseAuth:
            element.close();
            return;

        // Error occurred - leave form open
        case MCROrderRecapStatus::OpenError:
            return;

        // Leave form open, payment authorization issue.
        case MCROrderRecapStatus::OpenNoAuth:
            if (SalesTableExchange)
            {
                salesOrderTotalsExchange = new MCRSalesOrderTotals(SalesTableExchange);
                custPaymTotals = MCRCustPaymTotals::construct(SalesTableExchange.TableId, SalesTableExchange.RecId, salesOrderTotalsExchange);
                if (custPaymTotals.getOverallPaymStatus() == MCRCustPaymStatus::Declined)
                {
                    // If payment status is 'Declined' make the accept decline button
                    // visible
                    AcceptDeclineButtonExchange.visible(true);
                }
            }
            salesTableReturn.reread();
            salesTableReturn_ds.refresh();
            return;
    }
}
