/// <summary>
///     Enables fields and buttons based on the status of the royalty record.
/// </summary>
public void enableByStatus()
{
    boolean             allowedit;
    MCRRoyaltyTable     mcrRoyaltyTableLocal;
    MCRRoyaltyVendTable mcrRoyaltyVendTable;
    boolean             royaltyTableRecordExists;
    boolean             royaltyContractRecordExists;

    // allow edit if MCRRoyaltyContract.validated is no, AND no claims are found. Otherwise, lock down editing.
    if (MCRRoyaltyContract.Validated    == NoYes::No)
    {
        select firstonly RecId from mcrRoyaltyTableLocal
                where  mcrRoyaltyTableLocal.RoyaltyContract == MCRRoyaltyContract.RoyaltyContract
                exists join mcrRoyaltyVendTable
                    where mcrRoyaltyVendTable.RoyaltyCode   == mcrRoyaltyTableLocal.RoyaltyCode;
        if (!mcrRoyaltyTableLocal)
        {
            allowedit   = true;
        }
    }

    royaltyContractRecordExists = MCRRoyaltyContract.RecId;
    royaltyTableRecordExists = MCRRoyaltyTable.RecId;

    MCRRoyaltyContract_ds.allowEdit(allowedit);
    MCRRoyaltyContract_ds.allowDelete(allowedit);
    // child tables and buttons
    // If base table is approved, child tables cannot be edited, created, or deleted
    ButtonGroupItems.enabled(allowedit
                                 && royaltyContractRecordExists);
    MCRRoyaltyItemTable_ds.allowEdit(allowedit);
    MCRRoyaltyItemTable_ds.allowDelete(allowedit);
    MCRRoyaltyItemTable_ds.allowCreate(allowedit
                                           && royaltyContractRecordExists);

    ButtonGroupItemsCode.enabled(allowedit
                                     && royaltyTableRecordExists);
    MCRRoyaltyTable_ds.allowEdit(allowedit);
    MCRRoyaltyTable_ds.allowDelete(allowedit
                                       && royaltyContractRecordExists);
    MCRRoyaltyTable_ds.allowCreate(allowedit
                                       && royaltyContractRecordExists);

    MCRRoyaltyItemCode_ds.allowEdit(allowedit);
    MCRRoyaltyItemCode_ds.allowDelete(allowedit
                                          && royaltyTableRecordExists);
    MCRRoyaltyItemCode_ds.allowCreate(allowedit
                                          && royaltyTableRecordExists);

    MCRRoyaltyTablePricing_ds.allowEdit(allowedit);
    MCRRoyaltyTablePricing_ds.allowDelete(allowedit
                                              && royaltyTableRecordExists);
    MCRRoyaltyTablePricing_ds.allowCreate(allowedit
                                              && royaltyTableRecordExists);
}

public void init()
{
    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
    element.updateDesignCode(InventDimFormDesignUpdate::Init);

    if (element.args().parm())
    {
        parmRoyaltyContractCode = element.args().parm();
    }

    element.enableByStatus();
}

public void initControlPeriodType()
{
    boolean cumulateSalesByFlag = MCRRoyaltyContract.CumulateSalesBy == MCRRoyaltyCumulationPeriod::CustomizedPeriod;
    MCRRoyaltyContract_ds.object(fieldNum(MCRRoyaltyContract, RoyaltyCustomizedPeriodType)).enabled(cumulateSalesByFlag);
    MCRRoyaltyContract_ds.object(fieldNum(MCRRoyaltyContract, RoyaltyCustomizedPeriodType)).skip(!cumulateSalesByFlag);
}

